import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.Channel;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelGroup;
import io.netty.channel.ChannelHandlerContext;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.ChannelOption;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.channel.ChannelHandler.Sharable;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import io.netty.util.concurrent.GlobalEventExecutor;
import io.netty.util.ReferenceCountUtil;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.atomic.AtomicBoolean;

@Sharable
public abstract class BaseHttp2Server {
    private final SslContext sslContext;
    private final AtomicBoolean hasReleasedSslContext = new AtomicBoolean(false);
    private final ServerBootstrap bootstrap;
    private final boolean shouldShutDownEventLoopGroup;
    private final ChannelGroup allChannels = new DefaultChannelGroup(GlobalEventExecutor.INSTANCE);
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;

    public BaseHttp2Server(SslContext sslContext) {
        this(sslContext, null);
    }

    public BaseHttp2Server(SslContext sslContext, EventLoopGroup group) {
        this.sslContext = sslContext;
        this.bootstrap = new ServerBootstrap();
        this.shouldShutDownEventLoopGroup = group == null;
        this.bossGroup = group != null ? group : new NioEventLoopGroup(1);
        this.workerGroup = group != null ? group : new NioEventLoopGroup();

        this.bootstrap.group(this.bossGroup, this.workerGroup)
                     .channel(NioServerSocketChannel.class)
                     .childHandler(new ChannelInitializer<Channel>() {
                         @Override
                         protected void initChannel(Channel ch) {
                             ch.pipeline().addLast(new SslHandler(sslContext.newEngine(ch.alloc())));
                             ch.pipeline().addLast(new Http2MultiplexHandler(new Http2FrameHandler()));
                             addHandlersToPipeline(null, ch.pipeline());
                         }
                     })
                     .option(ChannelOption.SO_BACKLOG, 100)
                     .childOption(ChannelOption.SO_KEEPALIVE, true);
    }

    protected abstract void addHandlersToPipeline(SSLSession session, ChannelPipeline pipeline);

    public CompletableFuture<Integer> start(int port) {
        CompletableFuture<Integer> future = new CompletableFuture<>();
        bootstrap.bind(port).addListener(futureListener -> {
            if (futureListener.isSuccess()) {
                Channel channel = ((ChannelFuture)futureListener).channel();
                allChannels.add(channel);
                future.complete((Integer)channel.localAddress().getPort());
            } else {
                future.completeExceptionally(futureListener.cause());
            }
        });
        return future;
    }

    public CompletableFuture<Void> shutdown() {
        CompletableFuture<Void> future = new CompletableFuture<>();
        allChannels.close().addListener(future::complete);

        if (hasReleasedSslContext.compareAndSet(false, true)) {
            ReferenceCountUtil.release(sslContext);
        }

        if (shouldShutDownEventLoopGroup) {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }

        return future;
    }

    private class Http2FrameHandler extends SimpleChannelInboundHandler<Http2Frame> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, Http2Frame msg) {
            // Handle HTTP/2 frames
        }
    }
}

