import java.io.*;
import java.nio.*;
import java.nio.channels.*;
import java.nio.file.*;
import java.util.function.Consumer;

public class RandomAccessReadMemoryMappedFile implements AutoCloseable {
    private final FileChannel fileChannel;
    private final ByteBuffer mappedByteBuffer;
    private final long size;
    private final Consumer<ByteBuffer> unmapper;
    private boolean closed;

    public RandomAccessReadMemoryMappedFile(String filename) throws IOException {
        this(Paths.get(filename));
    }

    public RandomAccessReadMemoryMappedFile(File file) throws IOException {
        this(file.toPath());
    }

    public RandomAccessReadMemoryMappedFile(Path path) throws IOException {
        this.fileChannel = FileChannel.open(path, StandardOpenOption.READ);
        this.size = fileChannel.size();
        this.mappedByteBuffer = this.fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, this.size);
        this.closed = false;
        this.unmapper = createUnmapper(this.mappedByteBuffer);
    }

    private static Consumer<ByteBuffer> createUnmapper(ByteBuffer buffer) {
        try {
            Method getCleaner = buffer.getClass().getMethod("cleaner");
            getCleaner.setAccessible(true);
            Object cleaner = getCleaner.invoke(buffer);
            Method cleanMethod = cleaner.getClass().getMethod("clean");
            return (Consumer<ByteBuffer>) (buf) -> {
                cleanMethod.invoke(cleaner);
            };
        } catch (Exception e) {
            return null;
        }
    }

    @Override
    public void close() throws IOException {
        if (closed) {
            return;
        }
        closed = true;
        try {
            if (unmapper != null) {
                unmapper.accept(mappedByteBuffer);
            }
        } finally {
            try {
                fileChannel.close();
            } catch (IOException e) {
            }
        }
    }

    public void seek(long position) throws IOException {
        checkClosed();
        if (position < 0 || position > size) {
            throw new IllegalArgumentException("Position out of bounds: " + position);
        }
        mappedByteBuffer.position((int) position);
    }

    public long getPosition() {
        return (long) mappedByteBuffer.position();
    }

    public int read() throws IOException {
        checkClosed();
        if (isEOF()) {
            return -1;
        }
        return mappedByteBuffer.get() & 0xFF;
    }

    public int read(byte[] b, int off, int len) throws IOException {
        checkClosed();
        if (len == 0) {
            return 0;
        }
        long remaining = size - getPosition();
        if (remaining <= 0) {
            return -1;
        }
        int bytesToRead = (int) Math.min(len, remaining);
        mappedByteBuffer.get(b, off, bytesToRead);
        return bytesToRead;
    }

    public long length() {
        return size;
    }

    public boolean isClosed() {
        return closed;
    }

    public boolean isEOF() {
        return getPosition() >= size;
    }

    private void checkClosed() throws IOException {
        if (closed) {
            throw new IOException("Reader is closed");
        }
    }
}

