import java.util.concurrent.Future;

public abstract class AbstractScriptProcessor {

    protected static final ForkJoinPool POOL = new ForkJoinPool();
    protected static final String SH_SHELL = "/bin/sh";
    protected static final String CMD_SHELL = "cmd.exe";

    public abstract String getRunCommand();

    public ProcessResult executeScript(TaskContext context, String scriptPath) {
        try {
            File scriptFile = new File(scriptPath);
            if (!scriptFile.exists() || !scriptFile.canExecute()) {
                context.getLogger().error("Script not found or not executable: " + scriptPath);
                return new ProcessResult(1, "", "Script not found or not executable", true);
            }

            String osName = System.getProperty("os.name").toLowerCase();
            List<String> command = new ArrayList<>();
            command.add(getRunCommand());
            if (osName.contains("win")) {
                command.add("/c");
            }
            command.add(scriptPath);

            ProcessBuilder processBuilder = new ProcessBuilder(command);
            Process process = processBuilder.start();

            Charset charset = getCharset();
            OmsLogger logger = context.getLogger();
            StringBuilder stdout = new StringBuilder();
            StringBuilder stderr = new StringBuilder();

            Future<?> stdoutFuture = POOL.submit(() -> copyStream(process.getInputStream(), stdout, logger, charset));
            Future<?> stderrFuture = POOL.submit(() -> copyStream(process.getErrorStream(), stderr, logger, charset));

            int exitCode = process.waitFor();

            stdoutFuture.get();
            stderrFuture.get();

            return new ProcessResult(exitCode, stdout.toString(), stderr.toString(), exitCode == 0);
        } catch (Exception e) {
            context.getLogger().error("Error executing script: " + e.getMessage(), e);
            return new ProcessResult(1, "", "Error executing script: " + e.getMessage(), true);
        }
    }

    protected void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, charset))) {
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line).append("\n");
                omsLogger.info(line);
            }
        } catch (IOException e) {
            omsLogger.error("Error reading stream", e);
        }
    }

    protected Charset getCharset() {
        return Charset.forName("UTF-8");
    }
}
