        void handleOutput(String output);
        void handleCompletion(int exitCode);
        void handleError(String errorMessage);
    }

    public ProcessExecutor(Path scriptPath, Path libraryPath) {
        mProcessBuilder = new ProcessBuilder(scriptPath.toString());
        mProcessBuilder.environment().put("LD_LIBRARY_PATH", libraryPath.toString());
        mExecutor = (ThreadPoolExecutor) Executors.newCachedThreadPool();
    }

    public void execute(ProcessOutputHandler outputHandler) {
        try {
            mProcess = mProcessBuilder.start();
            mExecutor.submit(() -> {
                try (BufferedReader reader = new BufferedReader(new InputStreamReader(mProcess.getInputStream()))) {
                    String line;
                    while ((line = reader.readLine()) != null) {
                        outputHandler.handleOutput(line);
                    }
                } catch (IOException e) {
                    outputHandler.handleError(e.getMessage());
                }
            });

            mExecutor.submit(() -> {
                try (BufferedReader reader = new BufferedReader(new InputStreamReader(mProcess.getErrorStream()))) {
                    String line;
                    while ((line = reader.readLine()) != null) {
                        outputHandler.handleOutput(line);
                    }
                } catch (IOException e) {
                    outputHandler.handleError(e.getMessage());
                }
            });

            mExecutor.submit(() -> {
                try {
                    int exitCode = mProcess.waitFor();
                    outputHandler.handleCompletion(exitCode);
