    private ShellUtils() {}

    public static Process executeNoWait(Map<String, String> env, String... command) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(command);
        if (env != null) {
            Map<String, String> envMap = pb.environment();
            envMap.clear();
            envMap.putAll(env);
        }
        return pb.start();
    }

    public static Process sudoNoWait(Map<String, String> env, String... command) throws IOException {
        List<String> cmd = new ArrayList<>();
        cmd.add("su");
        cmd.add("-c");
        Collections.addAll(cmd, command);
        ProcessBuilder pb = new ProcessBuilder(cmd);
        if (env != null) {
            Map<String, String> envMap = pb.environment();
            envMap.clear();
            envMap.putAll(env);
        }
        return pb.start();
    }

    public static String waitFor(String command, Process process) throws IOException, InterruptedException {
        StringBuilder output = new StringBuilder();
        Thread stdoutThread = new Thread(() -> {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    output.append(line).append("\n");
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        });
        Thread stderrThread = new Thread(() -> {
