        mPreferences = preferences;

        PowerManager powerManager = (PowerManager) context.getSystemService(Context.POWER_SERVICE);
        mWakeLock = powerManager.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, "ProcessHandler::WakeLock");
        mWakeLock.setReferenceCounted(false);
    }

    public Process setupAndLaunch(HashMap<String, String> env, String[] command) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(command);
        if (mUseRoot) {
            List<String> newCommand = new ArrayList<>();
            newCommand.add("su");
            newCommand.add("-c");
            StringBuilder cmd = new StringBuilder();
            for (String part : command) {
                if (cmd.length() > 0) {
                    cmd.append(" ");
                }
                cmd.append(part);
            }
            newCommand.add(cmd.toString());
            pb = new ProcessBuilder(newCommand);
        }

        if (env != null) {
            Map<String, String> environment = pb.environment();
            environment.putAll(env);
        }

        return pb.start();
    }

    public Thread log(InputStream is, int priority) {
        return new Thread(() -> {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(is))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    switch (priority) {
                        case Log.VERBOSE:
                            Log.v("ProcessHandler", line);
                            break;
                        case Log.DEBUG:
                            Log.d("ProcessHandler", line);
                            break;
                        case Log.INFO:
                            Log.i("ProcessHandler", line);
                            break;
                        case Log.WARN:
                            Log.w("ProcessHandler", line);
                            break;
                        case Log.ERROR:
                            Log.e("ProcessHandler", line);
                            break;
                        default:
                            Log.d("ProcessHandler", line);
                    }
                }
            } catch (IOException e) {
                Log.e("ProcessHandler", "Error reading stream", e);
            }
        });
    }

    public String runProcess(ProcessBuilder pb, boolean returnStdOut) {
        mWakeLock.acquire();
        try {
            Process process = pb.start();
            StringBuilder stdout = new StringBuilder();
            Thread stdoutThread = null;

            if (returnStdOut) {
                stdoutThread = new Thread(() -> {
                    try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                        String line;
                        while ((line = reader.readLine()) != null) {
                            stdout.append(line).append("\n");
                            Log.i("ProcessHandler", line);
                        }
                    } catch (IOException e) {
                        Log.e("ProcessHandler", "Error reading stdout", e);
                    }
                });
                stdoutThread.start();
            } else {
                log(process.getInputStream(), Log.INFO).start();
            }

            Thread stderrThread = log(process.getErrorStream(), Log.ERROR);
            stderrThread.start();

            int exitCode = process.waitFor();

            if (stdoutThread != null) {
                stdoutThread.join();
            }

            if (returnStdOut) {
                return stdout.toString();
            } else {
                return Integer.toString(exitCode);
            }
        } catch (IOException | InterruptedException e) {
            Log.e("ProcessHandler", "Error running process", e);
            return null;
        } finally {
            mWakeLock.release();
        }
    }

    public List<String> getProcessPIDs(String processName) {
        List<String> pids = new ArrayList<>();
        ProcessBuilder pb = new ProcessBuilder("ps");
        if (mUseRoot) {
            List<String> newCommand = new ArrayList<>();
            newCommand.add("su");
            newCommand.add("-c");
            newCommand.add("ps");
            pb = new ProcessBuilder(newCommand);
        }

        try {
            Process process = pb.start();
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    if (line.contains(processName)) {
                        String[] parts = line.split("\\s+");
                        if (parts.length >= 2) {
                            pids.add(parts[1]);
                        }
                    }
                }
            }
        } catch (IOException e) {
            Log.e("ProcessHandler", "Error getting process PIDs", e);
        }

        return pids;
    }
}

