import io.lettuce.core.api.sync.RedisCommand;
import io.lettuce.core.api.reactive.RedisReactiveCommands;
import reactor.core.publisher.Flux;
import reactor.core.publisher.Mono;
import java.util.UUID;

public class RedisPublisher {
    private final RedisClient client;
    private final StatefulRedisConnection<String, String> connection;

    public RedisPublisher() {
        this.client = RedisClient.create("redis://localhost:6379");
        this.connection = client.connect();
        this.connection.sync().flushAll();
    }

    public java.util.concurrent.Flow.Publisher<String> createPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }
        String key = "list-" + UUID.randomUUID().toString();
        RedisCommand<String, String> sync = connection.sync();
        for (long i = 0; i < elements; i++) {
            sync.rpush(key, "element-" + i);
        }
        RedisReactiveCommands<String, String> reactive = connection.reactive();
        return reactive.lRange(key, 0, elements - 1)
                .flatMapMany(Flux::fromIterable)
                .onErrorResume(Mono::error)
                .materialize()
                .subscribeOn(java.lang.Thread.currentThread().getThreadFactory());
    }

    public void close() {
        try {
            connection.close();
            client.shutdown();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
