import redis.clients.jedis.*;
import java.util.concurrent.*;
import java.util.*;

public class RedisRegistry {
    private final ConcurrentHashMap<String, JedisPool> jedisPools = new ConcurrentHashMap<>();
    private final String clusterName;
    private final boolean replicate;
    private final ConcurrentMap<String, Notifier> notifiers = new ConcurrentHashMap<>();

    public RedisRegistry(AppContext appContext) {
        List<String> redisServers = appContext.getRedisServers();
        for (String server : redisServers) {
            JedisPool pool = new JedisPool(new JedisPoolConfig(), server);
            jedisPools.put(server, pool);
        }
        this.clusterName = appContext.getClusterName();
        this.replicate = appContext.isReplicate();
    }

    protected void doRegister(Node node) {
        String nodeKey = "cluster:" + clusterName + ":node:" + node.getPath();
        String nodeValue = node.serialize();

        if (replicate) {
            for (JedisPool pool : jedisPools.values()) {
                try (Jedis jedis = pool.getResource()) {
                    jedis.set(nodeKey, nodeValue);
                    jedis.publish("node-changes", node.getPath());
                } catch (Exception e) {
                    throw new NodeRegistryException("Registration failed on Redis server", e);
                }
            }
        } else {
            JedisPool primaryPool = jedisPools.values().iterator().next();
            try (Jedis jedis = primaryPool.getResource()) {
                jedis.set(nodeKey, nodeValue);
                jedis.publish("node-changes", node.getPath());
            } catch (Exception e) {
                throw new NodeRegistryException("Registration failed", e);
            }
        }
    }

    protected void doSubscribe(Node node, NotifyListener listener) {
        String nodePath = node.getPath();
        NotifySub notifySub = new NotifySub(listener, nodePath);
        Notifier notifier = new Notifier(notifySub, "node-changes");
        notifiers.put(nodePath, notifier);
        notifier.start();
    }

    public static class NotifySub extends JedisPubSub {
        private final NotifyListener listener;
        private final String nodePath;

        public NotifySub(NotifyListener listener, String nodePath) {
            this.listener = listener;
            this.nodePath = nodePath;
        }

        @Override
        public void onMessage(String channel, String message) {
            if (message.equals(nodePath)) {
                listener.onChange(nodePath);
            }
        }
    }

    public class Notifier extends Thread {
        private final JedisPubSub jedisPubSub;
        private final String channel;
        private volatile boolean running = true;
        private Jedis currentJedis;

        public Notifier(JedisPubSub jedisPubSub, String channel) {
            this.jedisPubSub = jedisPubSub;
            this.channel = channel;
        }

        @Override
        public void run() {
            JedisPool pool = jedisPools.values().iterator().next();
            while (running) {
                try {
                    currentJedis = pool.getResource();
                    currentJedis.subscribe(jedisPubSub, channel);
                } catch (Exception e) {
                    if (!running) break;
                    try {
                        Thread.sleep(1000);
                    } catch (InterruptedException ie) {
                        Thread.currentThread().interrupt();
                        break;
                    }
                } finally {
                    if (currentJedis != null) {
                        currentJedis.close();
                    }
                }
            }
        }

        public void shutdown() {
            running = false;
            if (currentJedis != null) {
                currentJedis.disconnect();
            }
            this.interrupt();
        }
    }
}
