public class RedisDao {
    private final JedisPool jedisPool;
    private final Schema<Seckill> schema;

    public RedisDao(String ip, int port) {
        this.jedisPool = new JedisPool(ip, port);
        this.schema = RuntimeSchema.getSchema(Seckill.class);
    }

    public Seckill getSeckill(long seckillId) {
        try (Jedis jedis = jedisPool.getResource()) {
            return getSeckill(seckillId, jedis);
        }
    }

    private Seckill getSeckill(long seckillId, Jedis jedis) {
        String key = getSeckillRedisKey(seckillId);
        byte[] keyBytes = key.getBytes(StandardCharsets.UTF_8);
        byte[] bytes = jedis.get(keyBytes);
        if (bytes == null || bytes.length == 0) {
            return null;
        }
        Seckill seckill = schema.newMessage();
        ProtostuffIOUtil.mergeFrom(bytes, seckill, schema);
        return seckill;
    }

    public Seckill getOrPutSeckill(long seckillId, Function<Long, Seckill> getDataFromDb) {
        Seckill seckill = getSeckill(seckillId);
        if (seckill != null) {
            return seckill;
        }

        String lockKey = "lock_seckill_" + seckillId;
        Jedis jedis = jedisPool.getResource();
        try {
            while (!jedis.set(lockKey, "locked", "NX", "PX", 30000).equals("OK")) {
                try {
                    Thread.sleep(100);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    return null;
                }
            }

            seckill = getSeckill(seckillId, jedis);
            if (seckill != null) {
                return seckill;
            }

            seckill = getDataFromDb.apply(seckillId);
            if (seckill != null) {
                putSeckill(seckill, jedis);
            }
        } finally {
            if (jedis.exists(lockKey)) {
                jedis.del(lockKey);
            }
            jedis.close();
        }
        return seckill;
    }

    public String putSeckill(Seckill seckill) {
        try (Jedis jedis = jedisPool.getResource()) {
            return putSeckill(seckill, jedis);
        }
    }

    private String putSeckill(Seckill seckill, Jedis jedis) {
        String key = getSeckillRedisKey(seckill.getSeckillId());
        byte[] keyBytes = key.getBytes(StandardCharsets.UTF_8);
        LinkedBuffer buffer = LinkedBuffer.allocate(LinkedBuffer.DEFAULT_BUFFER_SIZE);
