import java.nio.file.StandardOpenOption;

public class FileChannelProcessor {
    private final FileChannel fileChannel;
    private final long mappedAddress;
    private final long fileSize;

    public FileChannelProcessor(String filePath) throws IOException {
        this.fileChannel = FileChannel.open(Paths.get(filePath), StandardOpenOption.READ);
        this.fileSize = this.fileChannel.size();
        MappedByteBuffer buffer = this.fileChannel.map(FileChannel.MapMode.READ_ONLY, 0, this.fileSize);
        try {
            Field field = buffer.getClass().getDeclaredField("address");
            field.setAccessible(true);
            this.mappedAddress = field.getLong(buffer);
        } catch (Exception e) {
            throw new RuntimeException("Failed to access direct buffer address", e);
        }
    }

    public long getMappedAddress() {
        return mappedAddress;
    }

    public long getFileSize() {
        return fileSize;
    }

