public class JedisSentinelPool extends JedisPool {
    private static final Logger LOG = Logger.getLogger(JedisSentinelPool.class.getName());
    private final JedisClientConfig sentinelClientConfig;
    private final Collection<MasterListener> masterListeners;
    private volatile HostAndPort currentHostMaster;
    private final Lock initPoolLock = new ReentrantLock();

    public JedisSentinelPool(String masterName, Set<HostAndPort> sentinels, JedisFactory factory, JedisClientConfig sentinelClientConfig) {
        super(factory, sentinelClientConfig);
        this.sentinelClientConfig = sentinelClientConfig;
        this.masterListeners = initSentinels(sentinels, masterName);
    }

    private void initMaster(HostAndPort host) {
        initPoolLock.lock();
        try {
            if (currentHostMaster != null) {
                ((JedisPool) this).close();
            }
            currentHostMaster = host;
            super.setPool(new GenericObjectPool<>(getFactory(host), sentinelClientConfig));
        } finally {
            initPoolLock.unlock();
        }
    }

    private Collection<MasterListener> initSentinels(Set<HostAndPort> sentinels, String masterName) {
        Collection<MasterListener> listeners = new HashSet<>();
        for (HostAndPort sentinel : sentinels) {
            MasterListener listener = new MasterListener(masterName, sentinel.getHost(), sentinel.getPort());
            listener.start();
            listeners.add(listener);
        }
        return listeners;
    }

    private HostAndPort toHostAndPort(List<String> hostPort) {
        return new HostAndPort(hostPort.get(0), Integer.parseInt(hostPort.get(1)));
    }

    @Override
    public Jedis getResource() {
        while (currentHostMaster == null) {
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                throw new JedisException("Interrupted while waiting for master initialization", e);
            }
        }
        return super.getResource();
    }

    @Override
    public void returnResource(Jedis jedis) {
        super.returnResource(jedis);
    }

    class MasterListener extends Thread {
        private final String masterName;
        private final String host;
        private final int port;
        private final long subscribeRetryWaitTimeMillis = 5000;
        private final AtomicBoolean running = new AtomicBoolean(true);

        MasterListener(String masterName, String host, int port) {
            this.masterName = masterName;
            this.host = host;
            this.port = port;
        }

        public void run() {
            while (running.get()) {
                try (Jedis jedis = new Jedis(host, port)) {
                    jedis.connect();
                    jedis.psubscribe(new JedisPubSub() {
                        @Override
                        public void onPMessage(String pattern, String channel, String message) {
                            List<String> parts = List.of(message.split(" "));
                            if (parts.size() >= 5 && "+switch-master".equals(parts.get(0))) {
                                String newHost = parts.get(3);
                                int newPort = Integer.parseInt(parts.get(4));
                                HostAndPort newMaster = new HostAndPort(newHost, newPort);
                                JedisSentinelPool.this.initMaster(newMaster);
                            }
                        }
                    }, "+switch-master");
                } catch (Exception e) {
                    LOG.warning("Lost connection to Sentinel at " + host + ":" + port + ". Reconnecting...");
                    try {
                        Thread.sleep(subscribeRetryWaitTimeMillis);
                    } catch (InterruptedException ie) {
                        Thread.currentThread().interrupt();
                    }
                }
            }
        }

        public void shutdown() {
            running.set(false);
            interrupt();
        }
    }
}
