import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;

import java.util.ArrayList;
import java.util.List;
import java.util.Random;

public class ReadWriteRedisClient {
    private JedisPool master;
    private List<JedisPool> slaves = new ArrayList<>();
    private JedisPoolConfig jedisPoolConfig;
    private String address;
    private int timeout;
    private Random random = new Random();

    public void setJedisPoolConfig(JedisPoolConfig jedisPoolConfig) {
        this.jedisPoolConfig = jedisPoolConfig;
    }

    public void setTimeout(int timeout) {
        this.timeout = timeout;
    }

    public void setAddress(String address) {
        this.address = address;
    }

    public void initialize() {
        if (address == null || jedisPoolConfig == null) {
            throw new IllegalStateException("Address and jedisPoolConfig must be set before initialize");
        }
        String[] serverArray = address.split(",");
        for (int i = 0; i < serverArray.length; i++) {
            String[] hostPort = serverArray[i].split(":");
            String host = hostPort[0];
            int port = Integer.parseInt(hostPort[1]);
            if (i == 0) {
                master = new JedisPool(jedisPoolConfig, host, port, timeout);
            } else {
                slaves.add(new JedisPool(jedisPoolConfig, host, port, timeout));
            }
        }
    }

    public void destroy() {
        if (master != null) {
            master.close();
        }
        for (JedisPool pool : slaves) {
            pool.close();
        }
    }

    public String get(String key) {
        Jedis jedis = null;
        try {
            jedis = fetchResource(true);
            return jedis.get(key);
        } finally {
            if (jedis != null) {
                jedis.close();
            }
        }
    }

    public String set(String key, String value) {
        Jedis jedis = null;
        try {
            jedis = fetchResource(false);
            return jedis.set(key, value);
        } finally {
            if (jedis != null) {
                jedis.close();
            }
        }
    }

    private Jedis fetchResource(boolean read) {
        if (read && !slaves.isEmpty()) {
            JedisPool pool = slaves.get(random.nextInt(slaves.size()));
            return pool.getResource();
        } else {
            return master.getResource();
        }
    }
}

