        }
    }

    private void writeHeader() throws IOException {
        mFileChannel.write(getWaveHeader(mAudioFormat));
        mFileChannel.write(getFormatChunk(mAudioFormat));
        mFileChannel.write(getDataHeader());
    }

    @Override
    public void close() throws IOException {
        closeDataChunk();
        updateTotalSize();
        if (mFileChannel != null) {
            mFileChannel.close();
        }
    }

    public void close(Path path) throws IOException {
        closeDataChunk();
        updateTotalSize();
        Files.move(mFile, path, StandardCopyOption.REPLACE_EXISTING);
        if (mFileChannel != null) {
            mFileChannel.close();
        }
        mFile = path;
    }

    public void writeData(ByteBuffer buffer) throws IOException {
        if (mMaxSize > 0) {
            long currentSize = mFileChannel.size();
            int remaining = buffer.remaining();
            if (currentSize + remaining > mMaxSize) {
                rollover();
            }
        }

        int bytesToWrite = buffer.remaining();
        mFileChannel.write(buffer);
        mDataChunkSize += bytesToWrite;
    }

    private void closeDataChunk() throws IOException {
        if (mDataChunkOpen) {
            updateDataChunkSize();
            mDataChunkOpen = false;
        }
    }

    private void openDataChunk() throws IOException {
        ByteBuffer dataHeader = getDataHeader();
        mFileChannel.write(dataHeader);
        mDataChunkSizeOffset = mFileChannel.position() - 4;
        mDataChunkSize = 0;
        mDataChunkOpen = true;
    }

    private void rollover() throws IOException {
        closeDataChunk();
        updateTotalSize();
        mFileChannel.close();

        updateFileName();

        open();
    }

    private void updateFileName() {
        String fileName = mFile.getFileName().toString();
        int lastDotIndex = fileName.lastIndexOf('.');
        String baseName = lastDotIndex == -1 ? fileName : fileName.substring(0, lastDotIndex);
        String extension = lastDotIndex == -1 ? "" : fileName.substring(lastDotIndex);

        mFileRolloverCounter++;
        String newFileName = baseName + "_" + mFileRolloverCounter + extension;
        mFile = mFile.getParent().resolve(newFileName);
    }

    private void updateTotalSize() throws IOException {
        long totalSize = mFileChannel.size();
        long riffSize = totalSize - 8;
        ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(riffSize);
        mFileChannel.position(4);
        mFileChannel.write(sizeBuffer);
    }

    private void updateDataChunkSize() throws IOException {
        ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mDataChunkSize);
        mFileChannel.position(mDataChunkSizeOffset);
        mFileChannel.write(sizeBuffer);
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long value) {
        ByteBuffer buffer = ByteBuffer.allocate(4);
        buffer.putInt((int) value);
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer buffer = ByteBuffer.allocate(8);
        buffer.put("data".getBytes(StandardCharsets.US_ASCII));
        buffer.putInt(0);
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(12);
        buffer.put("RIFF".getBytes(StandardCharsets.US_ASCII));
        buffer.putInt(0);
        buffer.put("WAVE".getBytes(StandardCharsets.US_ASCII));
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(24);
        buffer.put("fmt ".getBytes(StandardCharsets.US_ASCII));
        buffer.putInt(16);
        buffer.putShort((short) 1);
        buffer.putShort((short) format.getChannels());
        buffer.putInt((int) format.getSampleRate());
        buffer.putInt((int) (format.getSampleRate() * format.getFrameSize()));
        buffer.putShort((short) format.getFrameSize());
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.flip();
        return buffer;
    }
}
