import redis.clients.jedis.util.DefaultRedisCredentials;
import org.apache.commons.pool2.impl.GenericObjectPoolConfig;

public class RedisConnectionManager {

    private JedisPooled jedisPool;
    private GenericObjectPoolConfig<Connection> poolConfig;

    public RedisConnectionManager(HostAndPort hostAndPort, DefaultJedisClientConfig clientConfig) {
        this.jedisPool = new JedisPooled(hostAndPort, clientConfig);
    }

    public RedisConnectionManager(HostAndPort hostAndPort, DefaultJedisClientConfig clientConfig, 
                                GenericObjectPoolConfig<Connection> poolConfig) {
        this.poolConfig = poolConfig;
        this.jedisPool = new JedisPooled(hostAndPort, clientConfig, poolConfig);
    }

    public Connection getConnection() throws JedisException {
        return jedisPool.getPool().getResource();
    }

    public void closeConnection(Connection connection) {
        if (connection != null) {
            connection.close();
        }
    }

    public void setCredentialsProvider(RedisCredentialsProvider credentialsProvider) {
        DefaultJedisClientConfig clientConfig = DefaultJedisClientConfig.builder()
            .credentialsProvider(credentialsProvider)
            .build();
        this.jedisPool = new JedisPooled(jedisPool.getPool().getAddress(), clientConfig);
    }

    public void executeCommand(String key, String value) {
        try (Connection connection = getConnection()) {
            connection.sendCommand(Command.SET, key, value);
            String response = connection.getStatusCodeReply();
            if (!"OK".equals(response)) {
                throw new JedisException("Failed to set key: " + key);
            }
        }
    }

    public String getValue(String key) {
        try (Connection connection = getConnection()) {
            connection.sendCommand(Command.GET, key);
            return connection.getBulkReply();
        }
    }

    public void close() {
        if (jedisPool != null) {
            jedisPool.close();
        }
    }
}

