import java.util.Set;
import java.util.List;
import java.util.concurrent.Thread;
import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisClientConfig;

public class SentineledConnectionProvider {
    private volatile HostAndPort currentMaster;
    private JedisPool pool;
    private final String masterName;
    private final Set<HostAndPort> sentinels;
    private final JedisClientConfig masterClientConfig;
    private final JedisClientConfig sentinelClientConfig;
    private SentinelListener listener;

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig, Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.sentinels = sentinels;
        this.masterClientConfig = masterClientConfig;
        this.sentinelClientConfig = sentinelClientConfig;
        this.currentMaster = initSentinels(sentinels);
        initMaster(currentMaster);
        this.listener = new SentinelListener();
        this.listener.start();
    }

    public Jedis getConnection() {
        return pool.getResource();
    }

    private void initMaster(HostAndPort master) {
        if (pool != null) {
            pool.close();
        }
        this.pool = new JedisPool(masterClientConfig, master.getHost(), master.getPort());
        this.currentMaster = master;
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        for (HostAndPort sentinel : sentinels) {
            try (Jedis jedis = new Jedis(sentinel.getHost(), sentinel.getPort(), sentinelClientConfig)) {
                List<String> masterAddress = jedis.sentinel("get-master-addr-by-name", masterName);
                if (masterAddress != null && masterAddress.size() >= 2) {
                    return new HostAndPort(masterAddress.get(0), Integer.parseInt(masterAddress.get(1)));
                }
            } catch (Exception e) {
            }
        }
        throw new IllegalStateException("No quorum of sentinels available to determine master.");
    }

    private class SentinelListener extends Thread {
        private volatile boolean running = true;

        @Override
        public void run() {
            while (running) {
                try {
                    Thread.sleep(5000);
                    HostAndPort newMaster = initSentinels(sentinels);
                    if (!newMaster.equals(currentMaster)) {
                        initMaster(newMaster);
                    }
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    break;
                } catch (Exception e) {
                }
            }
        }

        @Override
        public void interrupt() {
            running = false;
            super.interrupt();
        }
    }

    public void shutdown() {
        listener.interrupt();
        if (pool != null) {
            pool.close();
        }
    }
}
