class ArrayLongLongMapMmap {

  private final Path path;
  private FileChannel writeChannel;
  private MappedByteBuffer[] segmentsArray;
  private FileChannel readChannel = null;

  ArrayLongLongMapMmap(Path path, boolean madvise) {
    this.path = path;
    try {
      writeChannel = FileChannel.open(path, StandardOpenOption.WRITE, StandardOpenOption.CREATE);
    } catch (IOException e) {
      throw new UncheckedIOException(e);
    }
  }

  public void init() {
    try {
      writeChannel.close();
      readChannel = FileChannel.open(path, StandardOpenOption.READ);
      segmentsArray = ByteBufferUtil.mapFile(readChannel, readChannel.size(), 1L << 27, false, null);
    } catch (IOException e) {
      throw new UncheckedIOException(e);
    }
  }

  @Override
  public long get(long key) {
    long byteOffset = key << 3;
    int idx = (int) (byteOffset >>> 27);
    if (idx >= segmentsArray.length) {
      return 0;
    }
    MappedByteBuffer mappedByteBuffer = segmentsArray[idx];
    if (mappedByteBuffer == null) {
      return 0;
    }
    int offset = (int) (byteOffset & ((1L << 27) - 1));
    return mappedByteBuffer.getLong(offset);
  }

  @Override
  public void close() throws IOException {
    if (segmentsArray != null) {
      ByteBufferUtil.free(segmentsArray);
      segmentsArray = null;
    }
    if (writeChannel != null) {
      writeChannel.close();
      writeChannel = null;
