import java.io.IOException;
import java.lang.reflect.Method;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;
import java.nio.file.Files;

public class ArrayLongLongMapMmap {
    private final Path path;
    private FileChannel writeChannel;
    private MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = FileChannel.open(path, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE);
        this.segmentsArray = new MappedByteBuffer[0];
    }

    public void init() throws IOException {
        writeChannel.close();
        this.readChannel = FileChannel.open(path, StandardOpenOption.READ);
    }

    public long get(long key) {
        long segmentSizeBytes = 1 << 27;
        long entriesPerSegment = segmentSizeBytes / 8;
        long segmentIndex = key / entriesPerSegment;
        long posInSegment = key % entriesPerSegment;

        int currentIndex = (int) segmentIndex;
        if (currentIndex >= segmentsArray.length) {
            int newSize = Math.max(segmentsArray.length * 2, currentIndex + 1);
            MappedByteBuffer[] newSegments = new MappedByteBuffer[newSize];
            System.arraycopy(segmentsArray, 0, newSegments, 0, segmentsArray.length);
            segmentsArray = newSegments;
        }

        MappedByteBuffer buffer = segmentsArray[currentIndex];
        if (buffer == null) {
            long filePosition = segmentIndex * segmentSizeBytes;
            try {
                buffer = readChannel.map(FileChannel.MapMode.READ_ONLY, filePosition, segmentSizeBytes);
            } catch (IOException e) {
                throw new RuntimeException("Failed to map segment", e);
            }
            segmentsArray[currentIndex] = buffer;
        }

        long offset = posInSegment * 8;
        return buffer.getLong(offset);
    }

    public void close() throws IOException {
        for (MappedByteBuffer buffer : segmentsArray) {
            if (buffer != null) {
                unmap(buffer);
            }
        }
        if (readChannel != null) {
            readChannel.close();
        }
        if (writeChannel != null) {
            writeChannel.close();
        }
        Files.delete(path);
    }

    private void unmap(MappedByteBuffer buffer) {
        try {
            Method getCleaner = buffer.getClass().getMethod("cleaner");
            getCleaner.setAccessible(true);
            Object cleaner = getCleaner.invoke(buffer);
            Method clean = cleaner.getClass().getMethod("clean");
            clean.invoke(cleaner);
        } catch (Exception e) {
            // Handle exception silently
        }
    }
}
