import java.io.File;
import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.*;
import java.util.concurrent.atomic.AtomicBoolean;
import org.agrona.UnsafeBuffer;
import java.util.function.Consumer;
import java.util.function.IntConsumer;

public class MarkFile implements AutoCloseable {
    private final File markFile;
    private final MappedByteBuffer mappedBuffer;
    private final UnsafeBuffer buffer;
    private final AtomicBoolean isClosed = new AtomicBoolean(false);
    private final FileChannel fileChannel;

    public MarkFile(File markFile, boolean createNew, int versionSize, int bufferSize, int expectedVersion, long initialTimestamp, EpochClock clock, IntConsumer versionCallback, Consumer<String> errorCallback) throws IOException {
        this.markFile = markFile;
        this.fileChannel = FileChannel.open(markFile.toPath(), FileChannel.OpenOption.CREATE, FileChannel.OpenOption.READ, FileChannel.OpenOption.WRITE);
        if (createNew) {
            if (markFile.exists()) {
                if (!markFile.delete()) {
                    errorCallback.accept("Failed to delete existing file: " + markFile.getAbsolutePath());
                    throw new IOException("Failed to delete existing file");
                }
            }
            this.fileChannel.truncate(bufferSize);
        }
        this.mappedBuffer = this.fileChannel.map(FileChannel.MapMode.READ_WRITE, 0, bufferSize);
        this.buffer = new UnsafeBuffer(mappedBuffer);
        int storedVersion = buffer.getInt(0);
        if (storedVersion != expectedVersion) {
            versionCallback.accept(storedVersion);
            if (createNew) {
                buffer.putInt(0, expectedVersion);
            }
        }
        long storedTimestamp = buffer.getLong(4);
        long now = clock.millis();
        if (storedTimestamp == 0 || (now - storedTimestamp) > 60_000) {
            if (createNew) {
                buffer.putLong(4, initialTimestamp);
            }
        }
    }

    @Override
    public void close() {
        if (isClosed.compareAndSet(false, true)) {
            try {
                if (fileChannel != null) {
                    fileChannel.close();
                }
            } catch (IOException e) {
                System.err.println("Error closing file channel: " + e.getMessage());
            }
        }
    }

    public static MappedByteBuffer mapNewOrExistingMarkFile(File markFile, boolean createNew, int versionSize, int bufferSize, long expectedVersion, long initialTimestamp, EpochClock clock, IntConsumer versionCallback, Consumer<String> errorCallback) throws IOException {
        FileChannel fileChannel = FileChannel.open(markFile.toPath(), FileChannel.OpenOption.CREATE, FileChannel.OpenOption.READ, FileChannel.OpenOption.WRITE);
        if (createNew && markFile.exists()) {
            if (!markFile.delete()) {
                errorCallback.accept("Failed to delete existing file: " + markFile.getAbsolutePath());
                throw new IOException("Failed to delete existing file");
            }
        }
        if (fileChannel.size() < bufferSize) {
            fileChannel.truncate(bufferSize);
        }
        MappedByteBuffer buffer = fileChannel.map(FileChannel.MapMode.READ_WRITE, 0, bufferSize);
        int storedVersion = buffer.getInt(0);
        if (storedVersion != (int) expectedVersion) {
            versionCallback.accept(storedVersion);
            if (createNew) {
                buffer.putInt(0, (int) expectedVersion);
            }
        }
        long storedTimestamp = buffer.getLong(4);
        long now = clock.millis();
        if (storedTimestamp == 0 || (now - storedTimestamp) > 60_000) {
            if (createNew) {
                buffer.putLong(4, initialTimestamp);
            }
        }
        return buffer;
    }

    public static void ensureMarkFileLink(File markFile, File linkDir, String linkName) throws IOException {
        Path linkPath = Paths.get(linkDir.getAbsolutePath(), linkName);
        Path targetPath = markFile.toPath();
        if (Files.exists(linkPath)) {
            Path currentTarget = Files.readSymbolicLink(linkPath);
            if (!currentTarget.equals(targetPath)) {
                Files.delete(linkPath);
                Files.createSymbolicLink(linkPath, targetPath);
            }
        } else {
            Files.createSymbolicLink(linkPath, targetPath);
        }
    }
}

