import org.apache.flink.configuration.Configuration;
import org.apache.flink.streaming.api.functions.sink.RichSinkFunction;
import org.apache.flink.util.Preconditions;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.client.producer.SendCallback;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.client.exception.MQClientException;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.ArrayList;
import java.util.List;
import java.util.Properties;

public class RocketMQSink<IN> extends RichSinkFunction<IN> {
    private static final Logger LOG = LoggerFactory.getLogger(RocketMQSink.class);

    private final Properties props;
    private final TopicSelector<IN> topicSelector;
    private final KeyValueSerializationSchema<IN> serializationSchema;

    private DefaultMQProducer producer;
    private boolean async;
    private boolean batchFlushOnCheckpoint;
    private int batchSize;
    private int messageDeliveryDelayLevel;
    private List<Message> batchList;
    private boolean isClosed = false;

    public RocketMQSink(
            KeyValueSerializationSchema<IN> serializationSchema,
            TopicSelector<IN> topicSelector,
            Properties props) {
        this.serializationSchema = Preconditions.checkNotNull(serializationSchema);
        this.topicSelector = Preconditions.checkNotNull(topicSelector);
        this.props = Preconditions.checkNotNull(props);

        this.async = Boolean.parseBoolean(props.getProperty("async", "false"));
        this.batchFlushOnCheckpoint = Boolean.parseBoolean(props.getProperty("batch.flush.on.checkpoint", "false"));
        this.batchSize = Integer.parseInt(props.getProperty("batch.size", "100"));
        this.messageDeliveryDelayLevel = Integer.parseInt(props.getProperty("message.delay.level", "0"));
    }

    @Override
    public void open(Configuration parameters) throws Exception {
        super.open(parameters);
        producer = new DefaultMQProducer();
        producer.setProducerGroup(props.getProperty("producer.group"));
        producer.setNamesrvAddr(props.getProperty("namesrv.addr"));
        producer.start();
        if (batchFlushOnCheckpoint) {
            batchList = new ArrayList<>(batchSize);
        }
    }

    @Override
    public void invoke(IN value) throws Exception {
        if (isClosed) {
            return;
        }

        Message message = prepareMessage(value);

        if (batchFlushOnCheckpoint) {
            batchList.add(message);
            if (batchList.size() >= batchSize) {
                flushSync();
            }
        } else {
            send(message);
        }
    }

    private Message prepareMessage(IN value) {
        String topic = topicSelector.selectTopic(value);
        String tags = topicSelector.selectTags(value);

        byte[] body;
        try {
            body = serializationSchema.serialize(value);
        } catch (Exception e) {
            LOG.error("Failed to serialize message", e);
            throw new RuntimeException("Serialization failed", e);
        }

        Message msg = new Message(topic, tags, body);
        if (messageDeliveryDelayLevel > 0) {
            msg.setDelayTimeLevel(messageDeliveryDelayLevel);
        }
        return msg;
    }

    private void send(Message message) throws MQClientException, InterruptedException {
        if (async) {
            producer.send(message, new SendCallback() {
                @Override
                public void onSuccess(SendResult sendResult) {
                    LOG.debug("Message sent successfully: {}", sendResult);
                }

                @Override
                public void onException(Throwable throwable) {
                    LOG.error("Failed to send message", throwable);
                }
            });
        } else {
            producer.send(message);
        }
    }

    private void flushSync() throws MQClientException, InterruptedException {
        if (batchFlushOnCheckpoint && !batchList.isEmpty()) {
            for (Message msg : batchList) {
                send(msg);
            }
            batchList.clear();
        }
    }

    @Override
    public void close() {
        isClosed = true;
        if (batchFlushOnCheckpoint) {
            try {
                flushSync();
            } catch (Exception e) {
                LOG.error("Error while flushing messages", e);
            }
        }
        if (producer != null) {
            producer.shutdown();
        }
    }
}
