import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.MessageQueueSelector;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.common.message.MessageQueue;
import org.apache.rocketmq.remoting.common.RemotingHelper;

import java.io.UnsupportedEncodingException;
import java.util.List;

public class Producer {

    private DefaultMQProducer producer;

    public Producer(String producerGroup, String namesrvAddr) {
        this.producer = new DefaultMQProducer(producerGroup);
        this.producer.setNamesrvAddr(namesrvAddr);
    }

    public void start() throws Exception {
        producer.start();
    }

    public void shutdown() {
        producer.shutdown();
    }

    public SendResult sendOrderedMessage(String topic, String[] tags, String key, String body, int orderId) 
        throws Exception {
        Message msg = new Message(topic, tags[orderId % tags.length], key,
            body.getBytes(RemotingHelper.DEFAULT_CHARSET));

        SendResult sendResult = producer.send(msg, new MessageQueueSelector() {
            @Override
            public MessageQueue select(List<MessageQueue> mqs, Message msg, Object arg) {
                Integer id = (Integer) arg;
                int index = id % mqs.size();
                return mqs.get(index);
            }
        }, orderId);

        return sendResult;
    }
}

