import java.net.*;
import java.net.http.*;
import java.time.Duration;
import java.util.UUID;

public class BroadcastifyHttpClient {

    private final HttpClient mHttpClient;

    public BroadcastifyHttpClient() {
        mHttpClient = HttpClient.newBuilder()
                .version(HttpClient.Version.HTTP_2)
                .connectTimeout(Duration.ofSeconds(20))
                .followRedirects(HttpClient.Redirect.NORMAL)
                .build();
    }

    public String testConnection(BroadcastifyCallConfiguration configuration) {
        try {
            String boundary = "===" + UUID.randomUUID().toString() + "===";
            String body = "--" + boundary + "\r\n" +
                    "Content-Disposition: form-data; name=\"api_key\"\r\n\r\n" +
                    configuration.getApiKey() + "\r\n" +
                    "--" + boundary + "\r\n" +
                    "Content-Disposition: form-data; name=\"system_id\"\r\n\r\n" +
                    configuration.getSystemId() + "\r\n" +
                    "--" + boundary + "\r\n" +
                    "Content-Disposition: form-data; name=\"test\"\r\n\r\n" +
                    "true" + "\r\n" +
                    "--" + boundary + "--\r\n";

            HttpRequest request = HttpRequest.newBuilder()
                    .uri(URI.create("https://api.broadcastify.com/test"))
                    .header("Content-Type", "multipart/form-data; boundary=" + boundary)
                    .POST(HttpRequest.BodyPublishers.ofString(body))
                    .build();

            HttpResponse<String> response = mHttpClient.send(request, HttpResponse.BodyHandlers.ofString());

            if (response.statusCode() >= 200 && response.statusCode() < 300) {
                return "Status: " + response.statusCode() + "\nBody: " + response.body();
            } else {
                return "Error Status: " + response.statusCode() + "\nBody: " + response.body();
            }
        } catch (IOException | InterruptedException e) {
            return "Exception: " + e.getMessage();
        }
    }

    public void sendAsyncRequest(HttpRequest request) {
        mHttpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString())
                .thenAccept(response -> {
                    if (response.statusCode() == 200) {
                        System.out.println("Async Success: " + response.body());
                    } else {
                        System.err.println("Async Error: " + response.statusCode() + " - " + response.body());
                    }
                })
                .exceptionally(ex -> {
                    System.err.println("Async Exception: " + ex.getMessage());
                    return null;
                });
    }
}
