import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.client.producer.SendStatus;
import org.apache.rocketmq.client.AccessChannel;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.remoting.common.RemotingHelper;
import org.apache.rocketmq.logging.org.slf4j.Logger;
import org.apache.rocketmq.logging.org.slf4j.LoggerFactory;

public class RocketMQProducer {

    private static final Logger LOGGER = LoggerFactory.getLogger(RocketMQProducer.class);
    private DefaultMQProducer producer;
    private String namesrvAddr;

    public RocketMQProducer(String producerGroup, String namesrvAddr) {
        this.producer = new DefaultMQProducer(producerGroup, false, null);
        this.namesrvAddr = namesrvAddr;
    }

    public void configureProducer() {
        producer.setAccessChannel(AccessChannel.CLOUD);
        producer.setNamesrvAddr(namesrvAddr);
    }

    public void startProducer() throws Exception {
        producer.start();
        LOGGER.info("Producer Started...");
    }

    public SendResult sendMessage(String topic, String tags, String messageBody) throws Exception {
        Message msg = new Message(
            topic, 
            tags, 
            messageBody.getBytes(RemotingHelper.DEFAULT_CHARSET)
        );
        SendResult sendResult = producer.send(msg);
        if (sendResult.getSendStatus() != SendStatus.SEND_OK) {
            throw new Exception("Message sending failed");
        }
        return sendResult;
    }

    public void shutdownProducer() {
        producer.shutdown();
    }
}

