public class RocketMQTemplate {
    private static final InternalLogger log = InternalLoggerFactory.getLogger(RocketMQTemplate.class);
    private static final String DEFAULT_PRODUCER_GROUP = "DEFAULT_PRODUCER_GROUP";
    private static final int DEFAULT_QUEUE_NUM = 4;
    private static final int DEFAULT_SEND_MSG_TIMEOUT = 3000;
    private final Map<String, DefaultMQProducer> producerMap = new ConcurrentHashMap<>();

    public DefaultMQProducer getProducer() {
        return getProducer(DEFAULT_PRODUCER_GROUP, DEFAULT_SEND_MSG_TIMEOUT);
