    public DefaultMQProducer getProducer(String producerGroup, int sendMsgTimeout) {
        return getProducer(producerGroup, DEFAULT_QUEUE_NUM, sendMsgTimeout);
    }

    public DefaultMQProducer getProducer(int queueNum, String producerGroup, int sendMsgTimeout) {
        String key = producerGroup + "-" + queueNum + "-" + sendMsgTimeout;
        return producerMap.computeIfAbsent(key, k -> {
            DefaultMQProducer producer = new DefaultMQProducer(producerGroup);
            producer.setSendMsgTimeout(sendMsgTimeout);
            producer.setRetryTimesWhenSendFailed(3);
            try {
                producer.start();
            } catch (MQClientException | InterruptedException e) {
                log.error("Failed to start producer for group {}", producerGroup, e);
                throw new RuntimeException(e);
            }
            return producer;
        });
    }

    public SendResult send(String topic, String body) throws MQClientException, InterruptedException {
        return send(topic, body, DEFAULT_SEND_MSG_TIMEOUT);
    }

    public SendResult send(String topic, String body, int sendMsgTimeout) throws MQClientException, InterruptedException {
        return send(DEFAULT_PRODUCER_GROUP, topic, body, sendMsgTimeout);
    }

    public SendResult send(String producerGroup, String topic, String body, int sendMsgTimeout) throws MQClientException, InterruptedException {
        Message msg = new Message(topic, body.getBytes());
        return send(producerGroup, msg, sendMsgTimeout);
