    public SendResult send(String producerGroup, String topic, String tags, String body, int sendMsgTimeout) throws MQClientException, InterruptedException {
        Message msg = new Message(topic, tags, body.getBytes());
        return send(producerGroup, msg, sendMsgTimeout);
    }

    public SendResult send(String producerGroup, String topic, String tags, String keys, String body, int sendMsgTimeout) throws MQClientException, InterruptedException {
        Message msg = new Message(topic, tags, keys.getBytes(), body.getBytes());
        return send(producerGroup, msg, sendMsgTimeout);
    }

    private SendResult send(String producerGroup, Message message, int sendMsgTimeout) throws MQClientException, InterruptedException {
        DefaultMQProducer producer = getProducer(producerGroup, sendMsgTimeout);
        return producer.send(message, sendMsgTimeout);
    }

    public SendResult sendOrderly(String producerGroup, String topic, String keys, String body) throws MQClientException, InterruptedException {
        return sendOrderly(producerGroup, topic, keys, body, DEFAULT_SEND_MSG_TIMEOUT);
    }

    public SendResult sendOrderly(String producerGroup, String topic, String keys, String body, int sendMsgTimeout) throws MQClientException, InterruptedException {
        Message msg = new Message(topic, body.getBytes());
        msg.setKeys(keys);
        return sendOrderly(producerGroup, msg, keys, sendMsgTimeout);
    }

    public SendResult sendOrderly(String producerGroup, String topic, String tags, String keys, String body, int sendMsgTimeout) throws MQClientException, InterruptedException {
        Message msg = new Message(topic, tags, body.getBytes());
        msg.setKeys(keys);
        return sendOrderly(producerGroup, msg, keys, sendMsgTimeout);
    }

    private SendResult sendOrderly(String producerGroup, Message message, String shardingKey, int sendMsgTimeout) throws MQClientException, InterruptedException {
        DefaultMQProducer producer = getProducer(producerGroup, sendMsgTimeout);
        return producer.send(message, (mqs, msg, obj) -> {
            int index = Math.abs(obj.hashCode()) % mqs.size();
            return mqs.get(index);
        }, shardingKey, sendMsgTimeout);
    }
}
