import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.SendCallback;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.Message;
import org.apache.rocketmq.common.message.MessageQueueSelector;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.List;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.CopyOnWriteArrayList;
import java.util.concurrent.atomic.AtomicInteger;

public class RMQAsyncSendProducer extends AbstractMQProducer {
    private String nsAddr;
    private DefaultMQProducer producer;
    private SendCallback sendCallback;
    private List<SendResult> successSendResult = new CopyOnWriteArrayList<>();
    private AtomicInteger exceptionMsgCount = new AtomicInteger(0);
    private int msgSize;
    private Logger logger = LoggerFactory.getLogger(RMQAsyncSendProducer.class);
    private CountDownLatch latch;

    public RMQAsyncSendProducer(String nsAddr, String topic) {
        this.nsAddr = nsAddr;
        this.setTopic(topic);
        this.sendCallback = new SendCallback() {
            @Override
            public void onSuccess(SendResult sendResult) {
                successSendResult.add(sendResult);
                if (latch != null) {
                    latch.countDown();
                }
            }

            @Override
            public void onException(Throwable e) {
                exceptionMsgCount.incrementAndGet();
                if (latch != null) {
                    latch.countDown();
                }
            }
        };
    }

    public void create() {
        producer = new DefaultMQProducer("AsyncProducerGroup");
        producer.setNamesrvAddr(nsAddr);
    }

    public void start() throws MQClientException {
        producer.start();
    }

    public void asyncSend(Object msg) {
        Message rocketMsg = new Message(getTopic(), "TagA", msg.toString().getBytes());
        producer.send(rocketMsg, sendCallback);
    }

    public void asyncSend(int msgSize) {
        this.msgSize = msgSize;
        this.latch = new CountDownLatch(msgSize);
        for (int i = 0; i < msgSize; i++) {
            try {
                Message rocketMsg = new Message(getTopic(), "TagA", getMsgBodys().get(i));
                producer.send(rocketMsg, sendCallback);
            } catch (Exception e) {
                logger.error("Failed to send message: {}", e.getMessage());
            }
        }
    }

    public void asyncSend(Object msg, MessageQueueSelector selector, Object arg) {
        Message rocketMsg = new Message(getTopic(), "TagA", msg.toString().getBytes());
        producer.send(rocketMsg, selector, arg, sendCallback);
    }

    public void waitForResponse(int timeoutMills) {
        try {
            boolean done = latch.await(timeoutMills, java.util.concurrent.TimeUnit.MILLISECONDS);
            if (!done) {
                logger.warn("Timeout waiting for responses, some messages may still be processing");
            }
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            logger.error("Interrupted while waiting for responses", e);
        }
    }

    @Override
    public void shutdown() {
        if (producer != null) {
            producer.shutdown();
        }
    }
}

