                mechOid = new Oid(args[3]);
            } else {
                mechOid = new Oid("1.2.840.113554.1.2.2"); // Kerberos
            }
        } catch (GSSException e) {
            client.print("Invalid mechanism OID: " + e.getMessage());
            client.exit(1);
        }

        try {
            client.s = new Socket(host, port);
            GSSContext context = client.createCtxt(serverName, mechOid);
            client.sendMsgToPeer(context, "Hello, Server!");
            client.verifyMsgFromPeer(context, "Hello, Client!");
            client.exit(0);
        } catch (Exception e) {
            e.printStackTrace();
            client.exit(1);
        }
    }

    public GSSContext createCtxt(String serverName, Oid mechOid) throws GSSException, IOException {
        GSSManager manager = GSSManager.getInstance();
        GSSName server = manager.createName(serverName, GSSName.NT_HOSTBASED_SERVICE);
        GSSContext context = manager.createContext(server, mechOid, null, GSSContext.DEFAULT_LIFETIME);
        context.requestMutualAuth(true);
        context.requestConf(true);
        context.requestInteg(true);

        InputStream in = s.getInputStream();
        OutputStream out = s.getOutputStream();

        byte[] token = new byte[0];
        while (!context.isEstablished()) {
            token = context.initSecContext(token, 0, token.length);
            if (token != null) {
                writeToken(out, token);
            }
            if (!context.isEstablished()) {
                token = readToken(in);
            }
        }

        if (!context.isMutualAuthComplete()) {
            throw new GSSException(GSSException.FAILURE, -1, "Mutual authentication failed");
        }
        return context;
    }

    public void sendMsgToPeer(GSSContext aCtxt, String msg) throws IOException, GSSException {
        byte[] messageBytes = msg.getBytes(StandardCharsets.UTF_8);
        MessageProp prop = new MessageProp(aCtxt.isConfidentialityOn() ? 1 : 0, false);
        byte[] outgoingToken = aCtxt.wrap(messageBytes, 0, messageBytes.length, prop);
        writeToken(s.getOutputStream(), outgoingToken);
    }

    public void verifyMsgFromPeer(GSSContext aCtxt, String expectedMsg) throws IOException, GSSException {
        byte[] token = readToken(s.getInputStream());
        MessageProp prop = new MessageProp();
        byte[] plainText = aCtxt.unwrap(token, 0, token.length, prop);
        String receivedMsg = new String(plainText, StandardCharsets.UTF_8);
        if (!receivedMsg.equals(expectedMsg)) {
            throw new GSSException(GSSException.BAD_MIC, -1, "Received message does not match expected");
        }
    }

    public void usage() {
        print("Usage: java GSSClient <host> <port> <serverName> [mechanismOid]");
    }

    public void print(String msg) {
        System.out.println(msg);
    }

    public void exit(int status) {
        try {
            if (s != null) {
                s.close();
            }
        } catch (IOException e) {
            // Ignore
        }
        System.exit(status);
    }

    private void writeToken(OutputStream out, byte[] token) throws IOException {
        DataOutputStream dout = new DataOutputStream(out);
        dout.writeInt(token.length);
        if (token.length > 0) {
            dout.write(token);
        }
        dout.flush();
    }

    private byte[] readToken(InputStream in) throws IOException {
        DataInputStream din = new DataInputStream(in);
        int length = din.readInt();
        if (length < 0) {
            return new byte[0];
        }
        byte[] token = new byte[length];
        din.readFully(token);
        return token;
    }
}
