            "Authorization: Basic " + Base64.getEncoder().encodeToString((USER_NAME + ":" + PASSWORD).getBytes()) + "\r\n" +
            "Connection: close\r\n\r\n";

    private final WeakReference<RetainedFragment> mListener;

    public HttpGetTask(RetainedFragment retainedFragment) {
        mListener = new WeakReference<>(retainedFragment);
    }

