public class FileSender extends BaseTransfer implements Runnable {
    private String mServerIpAddress;
    private int mPort;
    private FileInfo mFileInfo;
    private Socket mSocket;
    private BufferedOutputStream mOutputStream;
    private OnSendListener mOnSendListener;

    public interface OnSendListener {
        void onStart();
        void onProgress(long bytesSent, long totalBytes);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable throwable, FileInfo fileInfo);
    }

    public FileSender(Context context, FileInfo fileInfo, String serverIpAddress, int port) {
        super(context);
        this.mServerIpAddress = serverIpAddress;
        this.mPort = port;
        this.mFileInfo = fileInfo;
    }

    public void setOnSendListener(OnSendListener listener) {
        this.mOnSendListener = listener;
    }

    @Override
    public void run() {
        try {
            init();
            if (mOnSendListener != null) {
                mOnSendListener.onStart();
            }
            parseHeader();
            parseBody();
            if (mOnSendListener != null) {
                mOnSendListener.onSuccess(mFileInfo);
            }
        } catch (IOException e) {
            if (mOnSendListener != null) {
                mOnSendListener.onFailure(e, mFileInfo);
            }
        } finally {
            finish();
        }
    }

    private void init() throws IOException {
        mSocket = new Socket();
        mSocket.connect(new InetSocketAddress(mServerIpAddress, mPort), 5000);
        mOutputStream = new BufferedOutputStream(mSocket.getOutputStream());
    }

    private void parseHeader() throws IOException {
        String header = "FILENAME: " + mFileInfo.getFileName() + "\nSIZE: " + mFileInfo.getFileSize() + "\n\n";
        mOutputStream.write(header.getBytes(java.nio.charset.StandardCharsets.UTF_8));
        mOutputStream.flush();
    }

    private void parseBody() throws IOException {
        File file = mFileInfo.getFile();
        long totalBytes = file.length();
        long bytesSent = 0;
        try (FileInputStream fis = new FileInputStream(file);
             BufferedInputStream bis = new BufferedInputStream(fis)) {
            byte[] buffer = new byte[8192];
            int bytesRead;
            while ((bytesRead = bis.read(buffer)) != -1) {
                mOutputStream.write(buffer, 0, bytesRead);
                bytesSent += bytesRead;
                if (mOnSendListener != null) {
                    mOnSendListener.onProgress(bytesSent, totalBytes);
                }
            }
        }
    }

    private void finish() {
        try {
            if (mOutputStream != null) {
                mOutputStream.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                if (mSocket != null) {
                    mSocket.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}
