import java.io.*;
import java.net.*;
import java.util.*;
import com.google.protobuf.*;

public class ProtobufIO {
    private Socket sock;
    private InputStream inputStream;
    private OutputStream outputStream;
    private Thread thread;
    private volatile boolean shutdown;
    private PrintStream outStream;
    private List<MessageCallback> callbacks = new ArrayList<>();
    private AtomicLong apiCallIdGen = new AtomicLong(0);
    private final Object CALLBACK_LOCK = new Object();

    public interface MessageCallback {
        void onError(long apiCallId, long errorCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap);
        void onWarning(long apiCallId, long warningCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap);
        void onInfo(long apiCallId, String message, String details, Map<String, String> objRefsMap);
        void onSuccess(long apiCallId, String message, String details, Map<String, String> objRefsMap);
    }

    public ProtobufIO(String host, int port, PrintStream out, String threadName) throws IOException {
        this.sock = new Socket(host, port);
        this.inputStream = sock.getInputStream();
        this.outputStream = sock.getOutputStream();
        this.outStream = out;
        this.thread = new Thread(new ProtobufIOWorker(), threadName);
        this.thread.start();
    }

    public long send(String apiCall, MessageLite msg) {
        long id = getNextApiCallId();
        send(id, apiCall, msg);
        return id;
    }

    public long send(long apiCallIdRef, String apiCallRef, MessageLite... messages) {
        for (MessageLite msg : messages) {
            try {
                byte[] data = msg.toByteArray();
                send(data);
            } catch (Exception e) {
                outStream.println("Error serializing message: " + e.getMessage());
                shutdown();
            }
        }
        return apiCallIdRef;
    }

    public void send(byte[] data) {
        synchronized (outputStream) {
            try {
                outputStream.write((data.length >>> 24) & 0xFF);
                outputStream.write((data.length >>> 16) & 0xFF);
                outputStream.write((data.length >>> 8) & 0xFF);
                outputStream.write(data.length & 0xFF);
                outputStream.write(data);
                outputStream.flush();
            } catch (IOException e) {
                outStream.println("Error sending data: " + e.getMessage());
                shutdown();
            }
        }
    }

    public void registerCallback(MessageCallback callback) {
        synchronized (CALLBACK_LOCK) {
            callbacks.add(callback);
        }
    }

    public void unregisterCallback(MessageCallback callback) {
        synchronized (CALLBACK_LOCK) {
            callbacks.remove(callback);
        }
    }

    public long getNextApiCallId() {
        return apiCallIdGen.incrementAndGet();
    }

    public void shutdown() {
        if (shutdown) return;
        shutdown = true;
        try {
            if (sock != null && !sock.isClosed()) sock.close();
        } catch (IOException e) {
            outStream.println("Error closing socket: " + e.getMessage());
        }
        if (thread != null) thread.interrupt();
    }

    private void callback(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap) {
        List<MessageCallback> currentCallbacks;
        synchronized (CALLBACK_LOCK) {
            currentCallbacks = new ArrayList<>(callbacks);
        }
        for (MessageCallback callback : currentCallbacks) {
            if (retCode < 0) {
                callback.onError(apiCallId, retCode, message, cause, correction, details, objRefsMap);
            } else if (retCode == 0) {
                callback.onSuccess(apiCallId, message, details, objRefsMap);
            } else if (retCode > 0) {
                callback.onWarning(apiCallId, retCode, message, cause, correction, details, objRefsMap);
            } else {
                callback.onInfo(apiCallId, message, details, objRefsMap);
            }
        }
    }

    private class ProtobufIOWorker implements Runnable {
        @Override
        public void run() {
            try {
                while (!shutdown) {
                    byte[] lengthBytes = new byte[4];
                    int bytesRead = 0;
                    while (bytesRead < 4 && !shutdown) {
                        int result = inputStream.read(lengthBytes, bytesRead, 4 - bytesRead);
                        if (result == -1) {
                            shutdown();
                            return;
                        }
                        bytesRead += result;
                    }
                    if (shutdown) break;

                    int length = ((lengthBytes[0] & 0xFF) << 24) |
                                 ((lengthBytes[1] & 0xFF) << 16) |
                                 ((lengthBytes[2] & 0xFF) << 8)  |
                                 (lengthBytes[3] & 0xFF);

                    byte[] data = new byte[length];
                    bytesRead = 0;
                    while (bytesRead < length && !shutdown) {
                        int result = inputStream.read(data, bytesRead, length - bytesRead);
                        if (result == -1) {
                            shutdown();
                            return;
                        }
                        bytesRead += result;
                    }
                    if (shutdown) break;

                    try {
                        // Replace with actual protobuf message parsing
                        ResponseMessage response = ResponseMessage.parseFrom(data);
                        long apiCallId = response.getApiCallId();
                        long retCode = response.getRetCode();
                        String msg = response.getMessage();
                        String cause = response.getCause();
                        String correction = response.getCorrection();
                        String details = response.getDetails();
                        Map<String, String> objRefsMap = response.getObjRefsMap();
                        callback(apiCallId, retCode, msg, cause, correction, details, objRefsMap);
                    } catch (InvalidProtocolBufferException e) {
                        outStream.println("Error parsing protobuf message: " + e.getMessage());
                    }
                }
            } catch (IOException e) {
                outStream.println("Worker thread error: " + e.getMessage());
                shutdown();
            } finally {
                shutdown();
            }
        }
    }
}

