import java.io.IOException;
import java.net.InetSocketAddress;
import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.ArrayList;
import java.util.List;

public class HttpCommunicationClient {
    private final InetSocketAddress address;
    private HttpClient httpClient;

    public HttpCommunicationClient(InetSocketAddress address) {
        this.address = address;
    }

    public void setupHttpClient(ThreadPool threadPool) {
        this.httpClient = HttpClient.newBuilder()
            .executor(threadPool.getExecutor())
            .followRedirects(HttpClient.Redirect.NORMAL)
            .build();
    }

    public void closeClient() {
        if (httpClient != null) {
            httpClient.close();
        }
    }

    public HttpResponse<String> executeAndDefaultAssertions(HttpRequest request) throws IOException, InterruptedException {
        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        if (response.statusCode() >= 400) {
            throw new RuntimeException("HTTP error: " + response.statusCode());
        }
        return response;
    }

    public HttpResponse<String> get(String path, String[]... headers) throws IOException, InterruptedException {
        HttpRequest request = buildRequest(path, HttpRequest.Method.GET, headers);
        return executeAndDefaultAssertions(request);
    }

    private HttpRequest buildRequest(String path, String method, String[]... headers) {
        URI uri = buildUri(path);
        HttpRequest.Builder builder = HttpRequest.newBuilder()
            .uri(uri)
            .method(method, HttpRequest.BodyPublishers.noBody());
        for (String[] header : headers) {
            if (header.length == 2) {
                builder.header(header[0], header[1]);
            }
        }
        return builder.build();
    }

    private URI buildUri(String path) {
        String host = address.getHostString();
        int port = address.getPort();
        String scheme = "http";
        if (port == 443) {
            scheme = "https";
        }
        return URI.create(String.format("%s://%s:%d%s", scheme, host, port, path));
    }

    public HttpResponse<String> browserGet(String uri) throws IOException, InterruptedException {
        String[] userHeader = browserHeader();
        HttpRequest request = HttpRequest.newBuilder()
            .uri(URI.create(uri))
            .header(userHeader[0], userHeader[1])
            .GET()
            .build();
        return executeAndDefaultAssertions(request);
    }

    public static String[] browserHeader() {
        return new String[]{"User-Agent", "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36"};
    }

    public HttpResponse<String> post(String path) throws IOException, InterruptedException {
        HttpRequest request = buildRequest(path, HttpRequest.Method.POST, new String[0][0]);
        return executeAndDefaultAssertions(request);
    }

    public List<String> getAllRedirectLocations(String path, String[]... headers) throws IOException, InterruptedException {
        List<String> redirectLocations = new ArrayList<>();
        HttpClient redirectClient = HttpClient.newBuilder()
            .followRedirects(HttpClient.Redirect.NEVER)
            .build();
        HttpRequest initialRequest = buildRequest(path, HttpRequest.Method.GET, headers);
        HttpResponse<String> response = redirectClient.send(initialRequest, HttpResponse.BodyHandlers.ofString());
        int statusCode = response.statusCode();
        while (statusCode >= 300 && statusCode < 400) {
            String location = response.headers().firstValue("Location").orElse(null);
            if (location == null) {
                break;
            }
            redirectLocations.add(location);
            URI currentUri = response.uri();
            URI newUri = currentUri.resolve(location);
            initialRequest = HttpRequest.newBuilder()
                .uri(newUri)
                .method("GET", HttpRequest.BodyPublishers.noBody())
                .build();
            response = redirectClient.send(initialRequest, HttpResponse.BodyHandlers.ofString());
            statusCode = response.statusCode();
        }
        return redirectLocations;
    }
}
