import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.Socket;

public class SocketClient {
    private static final SocketClient socketClient = new SocketClient();
    private Socket socket;
    private OutputStream outputStream;
    private InputStream inputStream;
    private boolean isConnected;
    private static final int DEFAULT_READ_BUFFER_SIZE = 32768;
    private byte[] buffer = new byte[DEFAULT_READ_BUFFER_SIZE];
    private byte[] receivedData = new byte[0];

    private SocketClient() {
    }

    public static SocketClient getInstance() {
        return socketClient;
    }

    public void openConnection(String ip, int port) throws IOException {
        if (isConnected) {
            closeConnection();
        }
        socket = new Socket(ip, port);
        outputStream = socket.getOutputStream();
        inputStream = socket.getInputStream();
        isConnected = true;
        receivedData = new byte[0];
    }

    public boolean isConnected() {
        return isConnected;
    }

    public void write(byte[] data) throws IOException {
        if (!isConnected) {
            throw new IOException("Not connected");
        }
        outputStream.write(data);
        outputStream.flush();
    }

    public int read(int bytesToBeRead) throws IOException {
        if (!isConnected) {
            throw new IOException("Not connected");
        }
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        int totalRead = 0;
        int remaining = bytesToBeRead;
        try {
            while (remaining > 0) {
                int read = inputStream.read(buffer, 0, Math.min(buffer.length, remaining));
                if (read == -1) {
                    break;
                }
                baos.write(buffer, 0, read);
                totalRead += read;
                remaining = bytesToBeRead - totalRead;
            }
        } catch (IOException e) {
            throw e;
        } finally {
            receivedData = baos.toByteArray();
        }
        return totalRead;
    }

    public byte[] getReceivedData() {
        return receivedData;
    }

    public void closeConnection() {
        try {
            if (inputStream != null) {
                inputStream.close();
            }
        } catch (IOException e) {
        }
        try {
            if (outputStream != null) {
                outputStream.close();
            }
        } catch (IOException e) {
        }
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
        }
        isConnected = false;
        receivedData = new byte[0];
    }
}

