                .exceptionally(ex -> retrievalFailed(ex));
    }

    private CompletableFuture<ReceivedKey> handleApiConfigResponse(HttpResponse<String> response) {
        if (response.statusCode() == 200) {
            return requestDeviceData();
        }
        return CompletableFuture.failedStage(new RuntimeException("API config error: " + response.statusCode()));
    }

    public CompletableFuture<ReceivedKey> requestDeviceData() {
        HttpRequest request = createRequest(hubConfig.getDeviceDataUri(), "GET");
        return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString())
                .thenCompose(response -> handleDeviceDataResponse(response))
                .exceptionally(ex -> retrievalFailed(ex));
    }

    private CompletableFuture<ReceivedKey> handleDeviceDataResponse(HttpResponse<String> response) {
        if (response.statusCode() == 200) {
            return requestVaultMasterkey("encryptedUserKey");
        }
        return CompletableFuture.failedStage(new RuntimeException("Device data error: " + response.statusCode()));
    }

    public CompletableFuture<ReceivedKey> requestVaultMasterkey(String encryptedUserKey) {
        HttpRequest request = createRequest(hubConfig.getVaultMasterkeyUri(), "POST")
                .header("Content-Type", "application/json")
                .POST(HttpRequest.BodyPublishers.ofString("{\"encryptedUserKey\":\"" + encryptedUserKey + "\"}"))
                .build();
        return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString())
                .thenApply(r -> handleVaultMasterkeyResponse(encryptedUserKey, r))
                .exceptionally(ex -> retrievalFailed(ex));
    }

    private ReceivedKey handleVaultMasterkeyResponse(String encryptedUserKey, HttpResponse<String> response) {
        if (response.statusCode() == 200) {
            return parseReceivedKey(response.body());
        }
        return retrievalFailed(new RuntimeException("Vault masterkey error: " + response.statusCode()));
    }

    public CompletableFuture<ReceivedKey> requestLegacyAccessToken() {
        HttpRequest request = createRequest(hubConfig.getLegacyTokenUri(), "POST")
                .header("Content-Type", "application/x-www-form-urlencoded")
                .POST(HttpRequest.BodyPublishers.ofString("device_id=" + deviceId))
                .build();
        return httpClient.sendAsync(request, HttpResponse.BodyHandlers.ofString())
                .thenApply(this::handleLegacyAccessTokenResponse)
                .exceptionally(ex -> retrievalFailed(ex));
    }

    private ReceivedKey handleLegacyAccessTokenResponse(HttpResponse<String> response) {
        if (response.statusCode() == 200) {
            return parseLegacyToken(response.body());
        }
        return retrievalFailed(new RuntimeException("Legacy token error: " + response.statusCode()));
    }

    private ReceivedKey retrievalFailed(Throwable cause) {
        return new ReceivedKey(ReceivedKey.Status.FAILED, cause.getMessage());
    }

    private HttpRequest createRequest(URI uri, String method) {
        return HttpRequest.newBuilder(uri)
                .header("Authorization", "Bearer " + bearerToken)
                .method(method, HttpRequest.BodyPublishers.noBody());
    }

    private ReceivedKey parseReceivedKey(String body) {
        return new ReceivedKey(ReceivedKey.Status.SUCCESS, body);
    }

    private ReceivedKey parseLegacyToken(String body) {
        return new ReceivedKey(ReceivedKey.Status.LEGACY, body);
    }
}

