import java.io.IOException;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.net.UnknownHostException;
import java.nio.ByteBuffer;
import java.nio.channels.SelectionKey;
import java.nio.channels.SocketChannel;
import java.util.Map;
import java.util.Queue;
import java.util.concurrent.atomic.AtomicReference;
import java.util.concurrent.LinkedBlockingQueue;

public class PassiveRedisIndexer {
    private final Queue<ByteBuffer> writeQueue = new LinkedBlockingQueue<>();
    private final AtomicReference<SocketHandler> ref = new AtomicReference<>();
    private final NIOConnectionManager conMan = new NIOConnectionManager();
    private final ConfigReader config = new ConfigReader();
    private static final Map<String, String> namespaces = new java.util.concurrent.ConcurrentHashMap<>();

    public InetAddress getAddress() throws UnknownHostException {
        return InetAddress.getByName(config.getServerAddress());
    }

    public class SocketHandler {
        private SocketChannel chan;
        private boolean awaitingWriteNotification;
        private ByteBuffer toWrite;
        private ByteBuffer oblivion = ByteBuffer.allocate(1024);

        public void open() throws IOException {
            chan = SocketChannel.open();
            chan.configureBlocking(false);
            InetAddress address = getAddress();
            chan.connect(new InetSocketAddress(address, config.getPort()));
        }

        public void close() {
            try {
                if (chan != null && chan.isOpen()) {
                    chan.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
            conMan.deregisterChannel(chan);
        }

        public SocketChannel getChannel() {
            return chan;
        }

        public void registrationEvent(NIOConnectionManager manager, SelectionKey key) {
            key.attach(this);
        }

        public void selectionEvent(SelectionKey key) {
            if (!key.isValid()) {
                close();
                return;
            }

            if (key.isConnectable()) {
                try {
                    if (chan.finishConnect()) {
                        key.interestOps(SelectionKey.OP_READ);
                    }
                } catch (IOException e) {
                    close();
                    return;
                }
            }

            if (key.isWritable()) {
                awaitingWriteNotification = false;
                while (!writeQueue.isEmpty()) {
                    if (toWrite == null) {
                        toWrite = writeQueue.poll();
                    }
                    if (toWrite == null) break;
                    try {
                        int bytesWritten = chan.write(toWrite);
                        if (toWrite.remaining() == 0) {
                            toWrite = null;
                        } else {
                            awaitingWriteNotification = true;
                            break;
                        }
                    } catch (IOException e) {
                        close();
                        return;
                    }
                }
                if (toWrite == null && !awaitingWriteNotification) {
                    key.interestOps(SelectionKey.OP_READ);
                }
            }

            if (key.isReadable()) {
                read();
            }
        }

        public void read() {
            try {
                int bytesRead = chan.read(oblivion);
                if (bytesRead < 0) {
                    close();
                }
                oblivion.clear();
            } catch (IOException e) {
                close();
            }
        }

        public void doStateChecks(long now) {
            if (!chan.isOpen() || !chan.isConnected()) {
                close();
            }
        }

        public int calcInterestOps() {
            int ops = SelectionKey.OP_READ;
            if (!writeQueue.isEmpty() || awaitingWriteNotification) {
                ops |= SelectionKey.OP_WRITE;
            }
            return ops;
        }
    }
}

