public class AsynchronousSocketChannelStream {
    private final ServerAddress serverAddress;
    private final InetAddressResolver inetAddressResolver;
    private final SocketSettings settings;
    private final AsynchronousChannelGroup group;
    private AsynchronousSocketChannel channel;

    public AsynchronousSocketChannelStream(ServerAddress serverAddress, InetAddressResolver inetAddressResolver, SocketSettings settings, AsynchronousChannelGroup group) {
        this.serverAddress = serverAddress;
        this.inetAddressResolver = inetAddressResolver;
        this.settings = settings;
        this.group = group;
    }

    public void openAsync(OperationContext operationContext, AsyncCompletionHandler<Void> handler) {
        List<SocketAddress> addresses = inetAddressResolver.resolve(serverAddress);
        Queue<SocketAddress> addressQueue = new LinkedList<>(addresses);
        initializeSocketChannel(handler, addressQueue);
    }

    private void initializeSocketChannel(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> addressQueue) {
        if (addressQueue.isEmpty()) {
            handler.failed(new IOException("No addresses available"), null);
            return;
        }
        SocketAddress address = addressQueue.poll();
        try {
            AsynchronousSocketChannel attemptChannel = group != null ? 
                AsynchronousSocketChannel.open(group) : 
                AsynchronousSocketChannel.open();
            configureSocketOptions(attemptChannel, settings);
            attemptChannel.connect(address, address, new OpenCompletionHandler(handler, addressQueue, attemptChannel));
        } catch (IOException e) {
            handler.failed(e, null);
        }
    }

    private void configureSocketOptions(AsynchronousSocketChannel channel, SocketSettings settings) throws IOException {
        if (settings.isSoKeepAlive()) channel.setOption(StandardSocketOptions.SO_KEEPALIVE, true);
        if (settings.isTcpNoDelay()) channel.setOption(StandardSocketOptions.TCP_NODELAY, true);
        if (settings.isSoReuseAddress()) channel.setOption(StandardSocketOptions.SO_REUSEADDR, true);
    }

    private class OpenCompletionHandler implements CompletionHandler<Void, SocketAddress> {
        private final AsyncCompletionHandler<Void> userHandler;
        private final Queue<SocketAddress> addressQueue;
        private final AsynchronousSocketChannel attemptChannel;

        public OpenCompletionHandler(AsyncCompletionHandler<Void> userHandler, Queue<SocketAddress> addressQueue, AsynchronousSocketChannel attemptChannel) {
            this.userHandler = userHandler;
            this.addressQueue = addressQueue;
            this.attemptChannel = attemptChannel;
        }

        @Override
        public void completed(Void result, SocketAddress attachment) {
            channel = attemptChannel;
            userHandler.completed(null, null);
        }

        @Override
        public void failed(Throwable exc, SocketAddress attachment) {
            try {
                attemptChannel.close();
            } catch (IOException e) {
                // Ignore
            }
            if (!addressQueue.isEmpty()) {
                initializeSocketChannel(userHandler, addressQueue);
            } else {
                userHandler.failed(exc, null);
            }
        }
    }

    public class AsynchronousSocketChannelAdapter implements ExtendedAsynchronousByteChannel {
        private final AsynchronousSocketChannel channel;

        public AsynchronousSocketChannelAdapter(AsynchronousSocketChannel channel) {
            this.channel = channel;
        }

        @Override
        public <A> void read(ByteBuffer dst, A attachment, CompletionHandler<Integer, ? super A> handler) {
            channel.read(dst, attachment, handler);
        }

        @Override
        public <A> void write(ByteBuffer src, A attachment, CompletionHandler<Integer, ? super A> handler) {
            channel.write(src, attachment, handler);
        }

        @Override
        public boolean isOpen() {
            return channel.isOpen();
        }

        @Override
        public void close() throws IOException {
            channel.close();
        }

        public Future<Integer> read(ByteBuffer dst, long timeout, TimeUnit unit) throws IOException {
            Future<Integer> future = channel.read(dst);
            try {
                if (!future.isDone()) {
                    if (!future.get(timeout, unit).isDone()) {
                        future.cancel(true);
                        throw new IOException("Read timed out");
                    }
                }
                return future;
            } catch (Exception e) {
                future.cancel(true);
                throw new IOException("Read operation failed", e);
            }
        }

        public Future<Integer> write(ByteBuffer src, long timeout, TimeUnit unit) throws IOException {
            Future<Integer> future = channel.write(src);
            try {
                if (!future.isDone()) {
                    if (!future.get(timeout, unit).isDone()) {
                        future.cancel(true);
                        throw new IOException("Write timed out");
                    }
                }
                return future;
            } catch (Exception e) {
                future.cancel(true);
                throw new IOException("Write operation failed", e);
            }
        }
    }
}
