public class NioTransport {
    private ServerSocketChannel srv_channel;
    private SocketChannel client_channel;
    private Receiver receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server;
    private boolean direct_buffers;
    private Log log = new Log();

    public interface RtReceiver {
        void onReceive(byte[] data);
    }

    public void options(String... options) {
        for (String opt : options) {
            if (opt.equals("server")) {
                server = true;
            } else if (opt.equals("client")) {
                server = false;
            } else if (opt.startsWith("host=")) {
                String hostStr = opt.substring(5);
                try {
                    host = InetAddress.getByName(hostStr);
                } catch (Exception e) {
                    log.error("Invalid host", e);
                }
            } else if (opt.startsWith("port=")) {
                try {
                    port = Integer.parseInt(opt.substring(5));
                } catch (Exception e) {
                    log.error("Invalid port", e);
                }
            } else if (opt.equals("direct_buffers")) {
                direct_buffers = true;
            }
        }
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public void start(String... options) {
        options(options);
        try {
            if (server) {
                srv_channel = ServerSocketChannel.open();
                srv_channel.configureBlocking(false);
                srv_channel.bind(new InetSocketAddress(port));
            } else {
                client_channel = SocketChannel.open();
            }
            receiver_thread = new Receiver();
            receiver_thread.start();
        } catch (IOException e) {
            log.error("Failed to start transport", e);
        }
    }

    public void stop() {
        try {
            if (srv_channel != null) {
                srv_channel.close();
            }
            if (client_channel != null) {
                client_channel.close();
            }
        } catch (IOException e) {
            log.error("Error closing channels", e);
        }
        if (receiver_thread != null) {
            receiver_thread.interrupt();
        }
    }

    public void send(Object dest, byte[] buf, int offset, int length) {
        SocketChannel channel;
        if (server) {
            channel = client_channel;
        } else {
            channel = client_channel;
        }
        if (channel != null && channel.isConnected()) {
            ByteBuffer buffer;
            if (direct_buffers) {
                buffer = ByteBuffer.allocateDirect(length);
            } else {
                buffer = ByteBuffer.allocate(length);
            }
            buffer.put(buf, offset, length);
            buffer.flip();
            try {
                channel.write(buffer);
            } catch (IOException e) {
                log.error("Error sending data", e);
            }
        }
    }

    private class Receiver extends Thread {
        @Override
        public void run() {
            try {
                Selector selector = Selector.open();
                if (server) {
                    srv_channel.register(selector, SelectionKey.OP_ACCEPT);
                } else {
                    client_channel.configureBlocking(false);
                    SocketAddress address = new InetSocketAddress(host, port);
                    client_channel.connect(address);
                    client_channel.register(selector, SelectionKey.OP_CONNECT);
                }

                while (!Thread.interrupted()) {
                    selector.select();
                    Set<SelectionKey> readyKeys = selector.selectedKeys();
                    Iterator<SelectionKey> iterator = readyKeys.iterator();
                    while (iterator.hasNext()) {
                        SelectionKey key = iterator.next();
                        iterator.remove();

                        if (key.isAcceptable()) {
                            ServerSocketChannel serverChannel = (ServerSocketChannel) key.channel();
                            SocketChannel clientChannel = serverChannel.accept();
                            clientChannel.configureBlocking(false);
                            clientChannel.register(selector, SelectionKey.OP_READ);
                            NioTransport.this.client_channel = clientChannel;
                        } else if (key.isConnectable()) {
                            SocketChannel channel = (SocketChannel) key.channel();
                            if (channel.finishConnect()) {
                                channel.register(selector, SelectionKey.OP_READ);
                            }
                        } else if (key.isReadable()) {
                            SocketChannel channel = (SocketChannel) key.channel();
                            ByteBuffer buffer;
                            if (direct_buffers) {
                                buffer = ByteBuffer.allocateDirect(1024);
                            } else {
                                buffer = ByteBuffer.allocate(1024);
                            }
                            int bytesRead = channel.read(buffer);
                            if (bytesRead > 0) {
                                buffer.flip();
                                byte[] data = new byte[buffer.remaining()];
                                buffer.get(data);
                                if (receiver != null) {
                                    receiver.onReceive(data);
                                }
                            } else if (bytesRead == -1) {
                                channel.close();
                            }
                        }
                    }
                }
            } catch (IOException e) {
                log.error("Error in receiver thread", e);
            }
        }
    }

    public static class Log {
        public void error(String message, Exception e) {
            System.err.println(message);
            e.printStackTrace();
        }
    }
}

