                    SelectionKey key = it.next();
                    it.remove();

                    if (key.isConnectable()) {
                        if (channel.finishConnect()) {
                            key.interestOps(SelectionKey.OP_WRITE);
                        }
                    } else if (key.isWritable()) {
                        write();
                        if (currentBuffer != null || !inner.isEmpty()) {
                            key.interestOps(SelectionKey.OP_WRITE);
                        } else {
                            key.interestOps(0);
                        }
                    }
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                if (channel != null) {
                    channel.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void write() throws IOException {
        while (true) {
            if (currentBuffer == null || !currentBuffer.hasRemaining()) {
                Packet packet = inner.poll();
                if (packet == null) {
                    return;
                }
                Checksum checksum = new CRC32();
                checksum.update(packet.getData(), 0, packet.getData().length);
                int checksumValue = (int) checksum.getValue();
                int dataLength = packet.getData().length;
                ByteBuffer buffer = ByteBuffer.allocate(4 + dataLength);
                buffer.putInt(checksumValue);
                buffer.put(packet.getData());
                buffer.flip();
                currentBuffer = buffer;
            }
            int bytesWritten = channel.write(currentBuffer);
            if (bytesWritten == -1) {
                throw new IOException("Connection closed by server");
            }
            if (!currentBuffer.hasRemaining()) {
                currentBuffer = null;
            } else {
                break;
            }
        }
    }
}

