public class ConnectionWorker implements Runnable {
    private final Logger logger;
    private volatile boolean stop;
    private final Selector selector;
    private final BlockingQueue<ConnectTask> myConnectQueue;
    private final BlockingQueue<WriteTask> myWriteQueue;
    private final Semaphore mySemaphore;
    private final List<KeyProcessor> myKeyProcessors;
    private final TimeService myTimeService;
    private long lastCleanupTime;
    private final int mySelectorTimeoutMillis;
    private final long myCleanupTimeoutMillis;
    private final CleanupProcessor myCleanupProcessor;
    private final NewConnectionAllower myNewConnectionAllower;

    public ConnectionWorker(Logger logger, List<KeyProcessor> keyProcessors, TimeService timeService,
                            CleanupProcessor cleanupProcessor, NewConnectionAllower newConnectionAllower,
                            int selectorTimeoutMillis, long cleanupTimeoutMillis) throws IOException {
        this.logger = logger;
        this.selector = Selector.open();
        this.myConnectQueue = new LinkedBlockingQueue<>();
        this.myWriteQueue = new LinkedBlockingQueue<>();
        this.mySemaphore = new Semaphore(1);
        this.myKeyProcessors = keyProcessors;
        this.myTimeService = timeService;
        this.mySelectorTimeoutMillis = selectorTimeoutMillis;
        this.myCleanupTimeoutMillis = cleanupTimeoutMillis;
        this.myCleanupProcessor = cleanupProcessor;
        this.myNewConnectionAllower = newConnectionAllower;
        this.lastCleanupTime = myTimeService.currentTimeMillis();
    }

    @Override
    public void run() {
        try {
            mySemaphore.acquire();
            try {
                while (!stop) {
                    connectToPeersFromQueue();
                    processSelectedKeys();
                    processWriteTasks();
                    if (needRunCleanup()) {
                        cleanup();
                    }
                }
            } finally {
                mySemaphore.release();
            }
        } catch (Exception e) {
            logger.error("Error in ConnectionWorker", e);
        }
    }

    private void connectToPeersFromQueue() {
        ConnectTask task;
        while ((task = myConnectQueue.poll()) != null) {
            if (myNewConnectionAllower.allowNewConnection()) {
                try {
                    SocketChannel channel = SocketChannel.open();
                    channel.configureBlocking(false);
                    InetSocketAddress address = new InetSocketAddress(task.getHost(), task.getPort());
                    channel.connect(address);
                    channel.register(selector, SelectionKey.OP_CONNECT);
                } catch (IOException e) {
                    logger.error("Failed to connect to {}:{}", task.getHost(), task.getPort(), e);
                }
            } else {
                myConnectQueue.add(task);
            }
        }
    }

    private void processSelectedKeys() throws IOException {
        int ready = selector.select(mySelectorTimeoutMillis);
        if (ready == 0) return;
        Iterator<SelectionKey> keys = selector.selectedKeys().iterator();
        while (keys.hasNext()) {
            SelectionKey key = keys.next();
            keys.remove();
            processSelectedKey(key);
        }
    }

    private void processSelectedKey(SelectionKey key) {
        if (!key.isValid()) {
            closeKey(key);
            return;
        }
        for (KeyProcessor processor : myKeyProcessors) {
            try {
                processor.process(key);
            } catch (Exception e) {
                logger.error("Error processing key", e);
                closeKey(key);
            }
        }
    }

    private void closeKey(SelectionKey key) {
        key.cancel();
        try {
            SocketChannel channel = (SocketChannel) key.channel();
            channel.close();
        } catch (IOException e) {
            logger.error("Error closing channel", e);
        }
    }

    public boolean offerConnect(ConnectTask task, int timeout, TimeUnit unit) {
        return addTaskToQueue(task, timeout, unit, myConnectQueue);
    }

    private <T> boolean addTaskToQueue(T task, int timeout, TimeUnit unit, BlockingQueue<T> queue) {
        try {
            return queue.offer(task, timeout, unit);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return false;
        }
    }

    private void processWriteTasks() {
        WriteTask task;
        while ((task = myWriteQueue.poll()) != null) {
            SelectionKey key = task.getSelectionKey();
            if (key != null && key.isValid()) {
                SocketChannel channel = (SocketChannel) key.channel();
                ByteBuffer buffer = task.getBuffer();
                try {
                    channel.write(buffer);
                    if (!buffer.hasRemaining()) {
                        buffer.clear();
                    }
                } catch (IOException e) {
                    logger.error("Error writing to channel", e);
                    closeKey(key);
                }
            }
        }
    }

    private void cleanup() {
        myCleanupProcessor.process();
        lastCleanupTime = myTimeService.currentTimeMillis();
    }

    private boolean needRunCleanup() {
        return myTimeService.currentTimeMillis() - lastCleanupTime >= myCleanupTimeoutMillis;
    }

    public void stopWorker() {
        this.stop = true;
        try {
            selector.wakeup();
        } catch (Exception e) {
            logger.error("Error waking up selector", e);
        }
    }
}
