        this.dhts = dhts;
        try {
            acceptor = ServerSocketChannel.open();
            acceptor.configureBlocking(false);
            acceptor.bind(new InetSocketAddress(SERVER_PORT));
            conMan = new NIOConnectionManager();
            conMan.register(acceptor, SelectionKey.OP_ACCEPT);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        try {
            if (acceptor != null) {
                acceptor.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void accept() {
        try {
            SocketChannel clientChannel = acceptor.accept();
            if (clientChannel != null) {
                clientChannel.configureBlocking(false);
                conMan.register(clientChannel, SelectionKey.OP_READ);
                SelectionKey key = clientChannel.keyFor(conMan.getSelector());
                key.attach(new ClientContext());
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void read() {
        SelectionKey key = getCurrentKey();
        if (key == null || !key.isValid()) {
            return;
        }
        SocketChannel channel = (SocketChannel) key.channel();
        ClientContext context = (ClientContext) key.attachment();
        ByteBuffer buffer = context.readBuffer;

        try {
            int bytesRead = channel.read(buffer);
            if (bytesRead == -1) {
                channel.close();
                return;
            }

            buffer.flip();
            if (buffer.remaining() >= 4) {
                int length = buffer.getInt();
                if (buffer.remaining() >= length) {
                    ByteBuffer payload = ByteBuffer.allocate(length);
                    payload.put(buffer);
                    buffer.compact();
                    process(payload);
                } else {
                    buffer.compact();
                }
            } else {
                buffer.compact();
            }
        } catch (IOException e) {
            e.printStackTrace();
            try {
                channel.close();
            } catch (IOException ex) {
                ex.printStackTrace();
            }
        }
    }

    public void process(ByteBuffer buf) {
        byte[] data = new byte[buf.remaining()];
        buf.get(data);
        String command = new String(data);
        for (DHT dht : dhts) {
            String response = dht.execute(command);
            ByteBuffer outBuf = ByteBuffer.wrap(response.getBytes());
            ClientContext context = (ClientContext) getCurrentKey().attachment();
            context.writeBuffer = outBuf;
            getCurrentKey().interestOps(SelectionKey.OP_WRITE);
        }
    }

    public void write() {
        SelectionKey key = getCurrentKey();
        if (key == null || !key.isValid()) {
            return;
        }
        SocketChannel channel = (SocketChannel) key.channel();
        ClientContext context = (ClientContext) key.attachment();
        ByteBuffer buffer = context.writeBuffer;

        try {
            int bytesWritten = channel.write(buffer);
            if (buffer.hasRemaining()) {
                key.interestOps(SelectionKey.OP_WRITE);
            } else {
                buffer.clear();
                key.interestOps(SelectionKey.OP_READ);
            }
        } catch (IOException e) {
            e.printStackTrace();
            try {
                channel.close();
            } catch (IOException ex) {
                ex.printStackTrace();
            }
        }
    }

    public int calcInterestOps() {
        SelectionKey key = getCurrentKey();
        ClientContext context = (ClientContext) key.attachment();
        return context.writeBuffer.position() > 0 ? SelectionKey.OP_WRITE : SelectionKey.OP_READ;
    }

    public void selectionEvent(SelectionKey key) {
        if (key.isAcceptable()) {
            accept();
        } else if (key.isReadable()) {
            read();
        } else if (key.isWritable()) {
            write();
        }
    }

    public void registrationEvent(NIOConnectionManager manager, SelectionKey key) {
        key.interestOps(calcInterestOps());
    }

    public SelectableChannel getChannel() {
        return acceptor;
    }

    public void doStateChecks(long now) {
    }

    private SelectionKey getCurrentKey() {
        return conMan.getCurrentKey();
    }

    private static class ClientContext {
        ByteBuffer readBuffer = ByteBuffer.allocate(4096);
        ByteBuffer writeBuffer = ByteBuffer.allocate(4096);
    }
}
