import java.util.logging.Logger;

class Server {
    private final ServerSocketChannel serverSocketChannel;
    private final Path socketPath;
    private static final Logger LOG = Logger.getLogger(Server.class.getName());
    private final Set<SocketChannel> clients = new HashSet<>();

    private Server(ServerSocketChannel serverSocketChannel, Path socketPath) {
        this.serverSocketChannel = serverSocketChannel;
        this.socketPath = socketPath;
    }

    public static Server create(Path socketPath) throws IOException {
        if (Files.exists(socketPath)) {
            Files.delete(socketPath);
        }
        ServerSocketChannel channel = ServerSocketChannel.open(StandardProtocolFamily.UNIX);
        channel.configureBlocking(false);
        channel.bind(UnixDomainSocketAddress.of(socketPath.toString()));
        return new Server(channel, socketPath);
    }

    public boolean isClient() {
        return false;
    }

    public void listen(IpcMessageListener listener, Executor executor) {
        executor.execute(() -> {
            try {
                while (serverSocketChannel.isOpen()) {
                    SocketChannel client = serverSocketChannel.accept();
                    if (client != null) {
                        synchronized (clients) {
                            clients.add(client);
                        }
                        client.configureBlocking(false);
                        readMessages(client, listener, executor);
                    }
                }
            } catch (IOException e) {
                LOG.log(Level.SEVERE, "Listen loop error", e);
            }
        });
    }

    private void readMessages(SocketChannel client, IpcMessageListener listener, Executor executor) {
        executor.execute(() -> {
            try {
                ByteBuffer buffer = ByteBuffer.allocate(1024);
                while (client.isOpen() && client.read(buffer) > 0) {
                    buffer.flip();
                    IpcMessage message = new IpcMessage(buffer.array(), 0, buffer.remaining());
                    listener.onMessage(message);
                    buffer.clear();
                }
                closeClient(client);
            } catch (IOException e) {
                LOG.log(Level.SEVERE, "Read error", e);
                closeClient(client);
            }
        });
    }

    private void closeClient(SocketChannel client) {
        try {
            client.close();
            synchronized (clients) {
                clients.remove(client);
            }
        } catch (IOException e) {
            LOG.log(Level.SEVERE, "Close client error", e);
        }
    }

    public void send(IpcMessage message, Executor executor) {
        ByteBuffer buffer = ByteBuffer.wrap(message.toByteArray());
        synchronized (clients) {
            for (SocketChannel client : new HashSet<>(clients)) {
                executor.execute(() -> {
                    try {
                        client.write(buffer);
                    } catch (IOException e) {
                        LOG.log(Level.SEVERE, "Send error", e);
                        closeClient(client);
                    }
                });
            }
        }
    }

    public void close() throws IOException {
        serverSocketChannel.close();
        try {
            Files.delete(socketPath);
        } catch (IOException e) {
            LOG.log(Level.SEVERE, "Socket file cleanup error", e);
            throw e;
        }
    }
