import java.nio.ByteBuffer;
import java.nio.channels.DatagramChannel;
import java.net.InetAddress;
import java.net.InetSocketAddress;
import java.util.concurrent.TransferQueue;
import java.util.concurrent.LinkedTransferQueue;
import java.util.concurrent.ThreadLocalRandom;
import java.util.concurrent.TimeUnit;

public class OpentrackerLiveSync {
    private final TransferQueue<ByteBuffer> toSend = new LinkedTransferQueue<>();
    private DatagramChannel channel;
    private final byte[] id;
    private volatile boolean running = false;
    private static final int HEADER_LENGTH = 8;
    private static final int PEER_LENGTH = 28;
    private static final int PEERS_PER_PACKET = 50;
    private static final String MULTICAST_ADDRESS = "224.0.0.1";
    private static final int MULTICAST_PORT = 12345;

    public OpentrackerLiveSync() {
        id = new byte[4];
        ThreadLocalRandom.current().nextBytes(id);
    }

    public void start() {
        try {
            channel = DatagramChannel.open();
            channel.configureBlocking(false);
            channel.bind(new InetSocketAddress(0));
            InetAddress group = InetAddress.getByName(MULTICAST_ADDRESS);
            channel.connect(new InetSocketAddress(group, MULTICAST_PORT));
            running = true;
            new Thread(this::sendLoop).start();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private void sendLoop() {
        while (running) {
            send();
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                break;
            }
        }
    }

    public void incomingPacket(MessageBase msg) {
        if (msg instanceof PeerRequest) {
            PeerRequest request = (PeerRequest) msg;
            InetAddress peerIP = request.getPeerIP();
            int peerPort = request.getPeerPort();
            ByteBuffer buffer = ByteBuffer.allocateDirect(PEER_LENGTH);
            byte[] ipBytes = ((Inet4Address) peerIP).getAddress();
            int ipInt = ((ipBytes[0] & 0xFF) << 24) | ((ipBytes[1] & 0xFF) << 16) | ((ipBytes[2] & 0xFF) << 8) | (ipBytes[3] & 0xFF);
            buffer.putInt(ipInt);
            buffer.putShort((short) peerPort);
            while (buffer.position() < PEER_LENGTH) {
                buffer.put((byte) 0);
            }
            buffer.flip();
            toSend.put(buffer);
        }
    }

    private void send() {
        try {
            int packetSize = HEADER_LENGTH + PEERS_PER_PACKET * PEER_LENGTH;
            ByteBuffer packetBuffer = ByteBuffer.allocateDirect(packetSize);
            packetBuffer.put(id);
            int peerCount = 0;
            packetBuffer.position(packetBuffer.position() + 4);
            while (peerCount < PEERS_PER_PACKET) {
                ByteBuffer peerBuffer = toSend.poll();
                if (peerBuffer == null) break;
                packetBuffer.put(peerBuffer);
                peerCount++;
            }
            int originalPosition = packetBuffer.position();
            packetBuffer.position(4);
            packetBuffer.putInt(peerCount);
            packetBuffer.position(originalPosition);
            packetBuffer.flip();
            if (packetBuffer.hasRemaining()) {
                channel.send(packetBuffer, null);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        running = false;
        try {
            if (channel != null) {
                channel.close();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

