import io.vertx.core.net.NetServerOptions;
import io.vertx.core.net.SocketAddress;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;

public class HAProxy {
    private final Logger log = LoggerFactory.getLogger(HAProxy.class);
    private String HOST = "localhost";
    private int PORT = 11080;
    private final SocketAddress remoteAddress;
    private final Buffer header;
    private NetServer server;
    private NetClient client;
    private SocketAddress connectionRemoteAddress;
    private SocketAddress connectionLocalAddress;

    public HAProxy(SocketAddress remoteAddress, Buffer header) {
        this.remoteAddress = remoteAddress;
        this.header = header;
    }

    public HAProxy start(Vertx vertx) throws Exception {
        this.server = vertx.createNetServer(new NetServerOptions());
        this.client = vertx.createNetClient(new NetClientOptions());

        server.connectHandler(clientSocket -> {
            clientSocket.pause();
            client.connect(remoteAddress).onSuccess(remoteSocket -> {
                log.debug("Connected to remote server at {}", remoteAddress);
                connectionLocalAddress = remoteSocket.localAddress();
                connectionRemoteAddress = remoteSocket.remoteAddress();
                remoteSocket.write(header);
                clientSocket.resume();
                clientSocket.pipeTo(remoteSocket);
                remoteSocket.pipeTo(clientSocket);
            }).onFailure(err -> {
                log.error("Failed to connect to remote server", err);
                clientSocket.close();
            });
        });

        final Exception[] startupException = new Exception[1];
        CountDownLatch latch = new CountDownLatch(1);

        server.listen(PORT, HOST).onSuccess(v -> {
            log.debug("HAProxy started on {}:{}", HOST, PORT);
            latch.countDown();
        }).onFailure(err -> {
            log.error("Failed to start HAProxy server", err);
            startupException[0] = new Exception("Failed to start HAProxy server", err);
            latch.countDown();
        });

        boolean started = latch.await(10, TimeUnit.SECONDS);
        if (!started) {
            throw new Exception("Server startup timed out");
        }
        if (startupException[0] != null) {
            throw startupException[0];
        }

        return this;
    }

    public void stop() {
        if (server != null) {
            server.close(ar -> {
                if (ar.succeeded()) {
                    log.debug("Server closed");
                } else {
                    log.error("Failed to close server", ar.cause());
                }
            });
        }
        if (client != null) {
            client.close();
        }
    }

    public SocketAddress getConnectionRemoteAddress() {
        return connectionRemoteAddress;
    }

    public SocketAddress getConnectionLocalAddress() {
        return connectionLocalAddress;
    }
}
