import org.apache.kafka.clients.admin.AdminClient;
import org.apache.kafka.clients.admin.DescribeConfigsOptions;
import org.apache.kafka.clients.admin.DescribeConfigsResult;
import org.apache.kafka.clients.admin.ListTopicsOptions;
import org.apache.kafka.clients.admin.ListTopicsResult;
import org.apache.kafka.common.config.Config;
import org.apache.kafka.common.config.ConfigEntry;
import org.apache.kafka.common.config.ConfigResource;
import org.apache.kafka.common.KafkaFuture;

import java.time.Duration;
import java.util.*;
import java.util.concurrent.ExecutionException;
import java.util.logging.Logger;
import java.util.stream.Collectors;

public class KafkaAdminTopicConfigProvider {
    private final Properties _clusterConfigs;
    private final AdminClient _adminClient;
    private static final Duration DESCRIBE_CLUSTER_CONFIGS_TIMEOUT = Duration.ofSeconds(90);
    private static final Logger LOG = Logger.getLogger(KafkaAdminTopicConfigProvider.class.getName());

    public KafkaAdminTopicConfigProvider(Properties clusterConfigs) {
        this._clusterConfigs = clusterConfigs;
        this._adminClient = AdminClient.create(clusterConfigs);
    }

    public Properties topicConfigs(String topic) {
        try {
            ConfigResource resource = new ConfigResource(ConfigResource.Type.TOPIC, topic);
            DescribeConfigsResult result = _adminClient.describeConfigs(Collections.singleton(resource), new DescribeConfigsOptions().timeoutMs((int) DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis()));
            Map<ConfigResource, Config> configs = result.all().get();
            if (configs.containsKey(resource)) {
                return convertConfigToProperties(configs.get(resource));
            }
        } catch (InterruptedException | ExecutionException e) {
            LOG.warning("Failed to retrieve config for topic " + topic + ": " + e.getMessage());
            Thread.currentThread().interrupt();
        } catch (Exception e) {
            LOG.warning("Unexpected error retrieving config for topic " + topic + ": " + e.getMessage());
        }
        return new Properties();
    }

    public Map<String, Properties> topicConfigs(Set<String> topics) {
        Map<String, Properties> result = new HashMap<>();
        if (topics == null || topics.isEmpty()) {
            return result;
        }
        try {
            Collection<ConfigResource> resources = topics.stream()
                .map(t -> new ConfigResource(ConfigResource.Type.TOPIC, t))
                .collect(Collectors.toList());
            DescribeConfigsResult describeResult = _adminClient.describeConfigs(resources, new DescribeConfigsOptions().timeoutMs((int) DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis()));
            Map<ConfigResource, Config> configs = describeResult.all().get();
            for (String topic : topics) {
                ConfigResource resource = new ConfigResource(ConfigResource.Type.TOPIC, topic);
                if (configs.containsKey(resource)) {
                    result.put(topic, convertConfigToProperties(configs.get(resource)));
                }
            }
        } catch (InterruptedException | ExecutionException e) {
            LOG.warning("Failed to retrieve configs for topics " + topics + ": " + e.getMessage());
            Thread.currentThread().interrupt();
        } catch (Exception e) {
            LOG.warning("Unexpected error retrieving configs for topics " + topics + ": " + e.getMessage());
        }
        return result;
    }

    public Map<String, Properties> allTopicConfigs() {
        Map<String, Properties> result = new HashMap<>();
        try {
            ListTopicsResult listResult = _adminClient.listTopics(new ListTopicsOptions().timeoutMs((int) DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis()));
            Set<String> topics = listResult.names().get();
            if (topics != null && !topics.isEmpty()) {
                Collection<ConfigResource> resources = topics.stream()
                    .map(t -> new ConfigResource(ConfigResource.Type.TOPIC, t))
                    .collect(Collectors.toList());
                DescribeConfigsResult describeResult = _adminClient.describeConfigs(resources, new DescribeConfigsOptions().timeoutMs((int) DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis()));
                Map<ConfigResource, Config> configs = describeResult.all().get();
                for (String topic : topics) {
                    ConfigResource resource = new ConfigResource(ConfigResource.Type.TOPIC, topic);
                    if (configs.containsKey(resource)) {
                        result.put(topic, convertConfigToProperties(configs.get(resource)));
                    }
                }
            }
        } catch (InterruptedException | ExecutionException e) {
            LOG.warning("Failed to retrieve all topic configs: " + e.getMessage());
            Thread.currentThread().interrupt();
        } catch (Exception e) {
            LOG.warning("Unexpected error retrieving all topic configs: " + e.getMessage());
        }
        return result;
    }

    private Properties convertConfigToProperties(Config config) {
        Properties props = new Properties();
        for (ConfigEntry entry : config.entries()) {
            if (entry.value() != null) {
                props.put(entry.name(), entry.value());
            }
        }
        return props;
    }
}

