import java.util.concurrent.atomic.AtomicInteger;

public class SocketActivation extends WebSocketServer {
    private final AtomicInteger clients = new AtomicInteger(0);

    public SocketActivation(ServerSocketChannel chan) {
        super(chan);
    }

    @Override
    public void onOpen(WebSocket conn, ClientHandshake handshake) {
        clients.incrementAndGet();
        String welcome = "Welcome to the WebSocket server!";
        conn.send(welcome);
        String joinMessage = "User " + conn.getRemoteSocketAddress().getAddress().getHostAddress() + " joined the chat.";
        for (WebSocket client : getConnections()) {
            client.send(joinMessage);
        }
    }

    @Override
    public void onClose(WebSocket conn, int code, String reason, boolean remote) {
        clients.decrementAndGet();
        String leaveMessage = "User " + conn.getRemoteSocketAddress().getAddress().getHostAddress() + " left the chat.";
        for (WebSocket client : getConnections()) {
            client.send(leaveMessage);
        }
        if (clients.get() == 0) {
            try {
                stop();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    @Override
    public void onMessage(WebSocket conn, String message) {
