@ServerEndpoint(value = "/websocket")
public class WebSocketServer {
    private static final Logger logger = Logger.getLogger(WebSocketServer.class.getName());
    private static volatile ConcurrentHashMap<String, Session> sessionPools = new ConcurrentHashMap<>();
    private static final AtomicInteger connectionCount = new AtomicInteger(0);

    @OnOpen
    public void onOpen(@PathParam("accessToken") String accessToken, Session session) {
        Session existingSession = sessionPools.putIfAbsent(accessToken, session);
        if (existingSession != null) {
            try {
                existingSession.close(new CloseReason(CloseReason.CloseCodes.CANNOT_ACCEPT, "Session replaced"));
            } catch (IOException e) {
                logger.log(Level.SEVERE, "Failed to close existing session", e);
            }
        }
        session.getUserProperties().put("accessToken", accessToken);
        connectionCount.incrementAndGet();
        logger.info("New connection: " + accessToken + ", total connections: " + connectionCount.get());
    }

    @OnClose
    public void onClose(Session session, CloseReason closeReason) {
        String accessToken = (String) session.getUserProperties().get("accessToken");
        if (accessToken != null) {
            sessionPools.remove(accessToken);
            connectionCount.decrementAndGet();
            logger.info("Connection closed: " + accessToken + ", reason: " + closeReason.getReasonPhrase() + 
                       ", total connections: " + connectionCount.get());
