@ServerEndpoint("/websocket")
public class WebsocketServerEndpoint {

    private static final Logger logger = Logger.getLogger(WebsocketServerEndpoint.class.getName());
    private static final Map<String, MessageHandler> HANDLERS = new HashMap<>();

    @OnOpen
    public void onOpen(Session session, EndpointConfig config) {
        String queryString = session.getRequestURI().getQuery();
        String accessToken = null;
        if (queryString != null) {
            String[] params = queryString.split("&");
            for (String param : params) {
                String[] pair = param.split("=");
                if (pair.length == 2 && "access_token".equals(pair[0])) {
                    accessToken = pair[1];
                    break;
                }
            }
        }
        if (accessToken == null || !isValidToken(accessToken)) {
            try {
                session.close(new CloseReason(CloseReason.CloseCodes.VIOLATED_POLICY, "Invalid access token"));
            } catch (Exception e) {
                logger.severe("Failed to close session: " + e.getMessage());
            }
        } else {
            logger.info("Client connected with access token: " + accessToken);
        }
    }

    private boolean isValidToken(String token) {
        return "valid_token".equals(token);
    }

    @OnMessage
    public void onMessage(Session session, String message) {
        try {
            String type = parseMessageType(message);
            MessageHandler handler = HANDLERS.get(type);
            if (handler != null) {
                Class<? extends Message> messageClass = getMessageClass(handler);
                Message msg = deserialize(message, messageClass);
                handler.handle(session, msg);
            } else {
                logger.warning("No handler found for message type: " + type);
            }
        } catch (Exception e) {
            logger.severe("Error processing message: " + e.getMessage());
            try {
                session.getBasicRemote().sendText("Error: " + e.getMessage());
            } catch (Exception ex) {
                logger.severe("Failed to send error message: " + ex.getMessage());
            }
        }
    }

    private String parseMessageType(String message) {
        return "dummy_type";
    }

    private Message deserialize(String json, Class<? extends Message> clazz) {
        try {
            return new ObjectMapper().readValue(json, clazz);
        } catch (Exception e) {
            throw new RuntimeException("Failed to deserialize message", e);
        }
    }

    @OnClose
    public void onClose(Session session, CloseReason closeReason) {
        logger.info("Client disconnected: " + closeReason.getReasonPhrase());
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.severe("WebSocket error: " + throwable.getMessage());
    }

    private Class<? extends Message> getMessageClass(MessageHandler handler) {
        Type[] interfaces = handler.getClass().getGenericInterfaces();
        for (Type type : interfaces) {
            if (type instanceof ParameterizedType) {
                ParameterizedType pType = (ParameterizedType) type;
                if (pType.getRawType() instanceof Class && ((Class<?>) pType.getRawType()).isAssignableFrom(MessageHandler.class)) {
                    Type[] typeArgs = pType.getActualTypeArguments();
                    if (typeArgs.length > 0 && typeArgs[0] instanceof Class) {
                        return (Class<? extends Message>) typeArgs[0];
                    }
                }
            }
        }
        throw new IllegalArgumentException("Handler does not have a valid generic type");
    }
}
