    private static final Map<Integer, byte[]> keyMap = new HashMap<>();
    private HttpSession httpSession;
    private Session session;
    private Long sessionId;
    private Process process;

    static {
        keyMap.put(8, new byte[]{(byte) 0x08});
        keyMap.put(127, new byte[]{(byte) 0x08});
    }

    @OnOpen
    public void onOpen(Session session, ServerEndpointConfig config) {
        this.session = session;
        this.httpSession = (HttpSession) session.getUserProperties().get("httpSession");
        this.sessionId = System.currentTimeMillis();
        try {
            process = new ProcessBuilder("bash").start();
            startOutputThread();
        } catch (IOException e) {
            log.severe("Failed to start terminal process: " + e.getMessage());
            try {
                session.close();
            } catch (IOException ex) {
                log.severe("Error closing session: " + ex.getMessage());
            }
        }
    }

    private void startOutputThread() {
        new Thread(() -> {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    session.getBasicRemote().sendText(line);
                }
            } catch (IOException e) {
                log.severe("Process output error: " + e.getMessage());
            }
        }).start();
    }

