            String type = json.getString("type");
            if ("command".equals(type)) {
                String cmd = json.getString("data") + "\n";
                process.getOutputStream().write(cmd.getBytes());
            } else if ("key".equals(type)) {
                int keyCode = json.getInt("data");
                byte[] bytes = keyMap.get(keyCode);
                if (bytes != null) {
                    process.getOutputStream().write(bytes);
                }
            }
            process.getOutputStream().flush();
        } catch (Exception e) {
            log.severe("Message processing error: " + e.getMessage());
        }
    }

    @OnError
    public void onError(Session session, Throwable t) {
        log.severe("WebSocket error: " + t.getMessage());
    }

    @OnClose
    public void onClose() {
        if (process != null) {
            process.destroy();
        }
    }
}

