import javax.websocket.OnMessage;
import javax.websocket.server.ServerEndpoint;
import javax.websocket.Session;
import java.io.IOException;
import java.io.InputStream;
import java.nio.ByteBuffer;

@ServerEndpoint("/inputstream")
public class WebSocketServerEndpoint {

    @OnMessage
    public void echoStream(InputStream stream, Session session) throws IOException {
        byte[] buffer = new byte[12];
        int totalBytesRead = 0;
        while (totalBytesRead < buffer.length) {
            int bytesRead = stream.read(buffer, totalBytesRead, buffer.length - totalBytesRead);
            if (bytesRead == -1) {
                throw new IOException("End of stream reached before reading 12 bytes");
            }
            totalBytesRead += bytesRead;
        }
        System.out.print("Received: ");
        for (byte b : buffer) {
            System.out.printf("%02x ", b & 0xFF);
        }
        System.out.println();
        session.getBasicRemote().sendBinary(ByteBuffer.wrap(buffer));
    }
}
