@ServerEndpoint(value = "/websocket/{name}")
public class WebSocket {

    private Session session;
    private String name;
    private static ConcurrentHashMap<String, WebSocket> webSocketSet = new ConcurrentHashMap<>();

    @OnOpen
    public void OnOpen(Session session, @PathParam("name") String name) {
        this.session = session;
        this.name = name;
        webSocketSet.put(name, this);
        GroupSending("User " + name + " connected.");
    }

    @OnMessage
    public void OnMessage(String messageStr) {
        if (messageStr.startsWith("@")) {
            String[] parts = messageStr.split(" ", 2);
            String targetName = parts[0].substring(1);
            String message = parts.length > 1 ? parts[1] : "";
            AppointSending(targetName, message);
        } else {
            GroupSending(messageStr);
        }
    }

    @OnClose
    public void OnClose() {
        webSocketSet.remove(name);
        GroupSending("User " + name + " disconnected.");
    }

    @OnError
    public void onError(Session session, Throwable error) {
        error.printStackTrace();
    }

